pragma solidity ^0.5.16;

contract Migrations {
  address public owner;
  uint public last_completed_migration;

  modifier restricted() {
    if (msg.sender == owner) _;
  }

  constructor() public {
    owner = msg.sender;
  }

  function setCompleted(uint completed) public restricted {
    last_completed_migration = completed;
  }

  function upgrade(address new_address) public restricted {
    Migrations upgraded = Migrations(new_address);
    upgraded.setCompleted(last_completed_migration);
  }
}

contracts/MultiCDPGetter.sol.content:
pragma solidity ^0.5.16;
pragma experimental ABIEncoderV2;

import "./CDPManager.sol";
import "./SortedCDPs.sol";

contract MultiCDPGetter {
    struct CombinedCDPData {
        address owner;

        uint debt;
        uint coll;
        uint stake;

        uint snapshotETH;
        uint snapshotCLVDebt;
    }

    CDPManager cdpManager; // XXX CDPs missing from ICDPManager?
    ISortedCDPs sortedCDPs;

    constructor(CDPManager _cdpManager, ISortedCDPs _sortedCDPs) public {
        cdpManager = _cdpManager;
        sortedCDPs = _sortedCDPs;
    }

    function min(uint a, uint b) internal pure returns (uint) {
        return a < b ? a : b;
    }

    function getMultipleSortedCDPs(int _startIdx, uint _count)
        external view returns (CombinedCDPData[] memory _cdps)
    {
        uint startIdx;
        bool descend;

        if (_startIdx >= 0) {
            startIdx = uint(_startIdx);
            descend = true;
        } else {
            startIdx = uint(-(_startIdx + 1));
            descend = false;
        }

        uint sortedCDPsSize = sortedCDPs.getSize();

        if (startIdx >= sortedCDPsSize) {
            _cdps = new CombinedCDPData[](0);
        } else {
            uint maxCount = sortedCDPsSize - startIdx;

            if (_count > maxCount) {
                _count = maxCount;
            }

            if (descend) {
                _cdps = getMultipleSortedCDPsFromHead(startIdx, _count);
            } else {
                _cdps = getMultipleSortedCDPsFromTail(startIdx, _count);
            }
        }
    }

    function getMultipleSortedCDPsFromHead(uint _startIdx, uint _count)
        internal view returns (CombinedCDPData[] memory _cdps)
    {
        address currentCDPowner = sortedCDPs.getFirst();

        for (uint idx = 0; idx < _startIdx; ++idx) {
            currentCDPowner = sortedCDPs.getNext(currentCDPowner);
        }

        _cdps = new CombinedCDPData[](_count);

        for (uint idx = 0; idx < _count; ++idx) {
            _cdps[idx].owner = currentCDPowner;
            (
                _cdps[idx].debt,
                _cdps[idx].coll,
                _cdps[idx].stake,
                /* status */,
                /* arrayIndex */
            ) = cdpManager.CDPs(currentCDPowner);
            (
                _cdps[idx].snapshotETH,
                _cdps[idx].snapshotCLVDebt
            ) = cdpManager.rewardSnapshots(currentCDPowner);

            currentCDPowner = sortedCDPs.getNext(currentCDPowner);
        }
    }

    function getMultipleSortedCDPsFromTail(uint _startIdx, uint _count)
        internal view returns (CombinedCDPData[] memory _cdps)
    {
        address currentCDPowner = sortedCDPs.getLast();

        for (uint idx = 0; idx < _startIdx; ++idx) {
            currentCDPowner = sortedCDPs.getPrev(currentCDPowner);
        }

        _cdps = new CombinedCDPData[](_count);

        for (uint idx = 0; idx < _count; ++idx) {
            _cdps[idx].owner = currentCDPowner;
            (
                _cdps[idx].debt,
                _cdps[idx].coll,
                _cdps[idx].stake,
                /* status */,
                /* arrayIndex */
            ) = cdpManager.CDPs(currentCDPowner);
            (
                _cdps[idx].snapshotETH,
                _cdps[idx].snapshotCLVDebt
            ) = cdpManager.rewardSnapshots(currentCDPowner);

            currentCDPowner = sortedCDPs.getPrev(currentCDPowner);
        }
    }
}

contracts/SortedCDPs.sol.content:
pragma solidity ^0.5.16;

import "./Interfaces/ISortedCDPs.sol";
import "./Interfaces/ICDPManager.sol";
import "./Interfaces/IBorrowerOperations.sol";
import "./Dependencies/SafeMath.sol";
import "./Dependencies/Ownable.sol";
import "./Dependencies/console.sol";

/* 
A sorted doubly linked list with nodes sorted in descending order, based on current ICRs of active CDPs. 
Optionally accepts insert position hints.

ICRs are computed dynamically at runtime, and not stored on the Node. This is because ICRs of active CDPs 
change dynamically as liquidation events occur.

The list relies on the fact that liquidation events preserve ordering: a liquidation decreases the ICRs of all active CDPs, 
but maintains their order. A node inserted based on current ICR will maintain the correct position, 
relative to it's peers, as rewards accumulate. Thus, Nodes remain sorted by current ICR.

Nodes need only be re-inserted upon a CDP operation - when the owner adds or removes collateral or debt.

The list is a modification of the following audited SortedDoublyLinkedList:
https://github.com/livepeer/protocol/blob/master/contracts/libraries/SortedDoublyLL.sol

In our variant, keys have been removed, and all ICR checks in functions now compare an ICR argument to the current ICR, 
calculated at runtime. Data is stored in the 'data' state variable.
*/
contract SortedCDPs is Ownable, ISortedCDPs {
    using SafeMath for uint256;

    event CDPManagerAddressChanged(address _newCDPlManagerAddress);
    event BorrowerOperationsAddressChanged(address _borrowerOperationsAddress);

    IBorrowerOperations borrowerOperations;
    address public borrowerOperationsAddress;

    ICDPManager cdpManager;
    address public CDPManagerAddress;

    // Information for a node in the list
    struct Node {
        bool exists;
        address nextId;                  // Id of next node (smaller ICR) in the list
        address prevId;                  // Id of previous node (larger ICR) in the list
    }

    // Information for the list
    struct Data {
        address head;                        // Head of the list. Also the node in the list with the largest ICR
        address tail;                        // Tail of the list. Also the node in the list with the smallest ICR
        uint256 maxSize;                     // Maximum size of the list
        uint256 size;                        // Current size of the list
        mapping (address => Node) nodes;     // Track the corresponding ids for each node in the list
    }

    Data data;

    // --- Modifiers ---

    modifier onlyBorrowerOperations() {
        require(_msgSender() == borrowerOperationsAddress, "SortedCDPs: Caller is not the BorrowerOperations contract");
        _;
    }

    modifier onlyCDPManager() {
        require(_msgSender() == CDPManagerAddress, "SortedCDPs: Caller is not the CDPManager");
        _;
    }

     modifier onlyBMorCDPM() {
        address sender = _msgSender();
        require(sender == CDPManagerAddress || sender == borrowerOperationsAddress, 
                "SortedCDPs: Caller is neither BM nor CDPM");
        _;
    }

    // --- Dependency setters --- 

    function setCDPManager(address _CDPManagerAddress) public onlyOwner {
        CDPManagerAddress = _CDPManagerAddress;
        cdpManager = ICDPManager(_CDPManagerAddress);
        emit CDPManagerAddressChanged(_CDPManagerAddress);
    }

    function setBorrowerOperations(address _borrowerOperationsAddress) public onlyOwner {
        borrowerOperationsAddress = _borrowerOperationsAddress;
        borrowerOperations = IBorrowerOperations(_borrowerOperationsAddress);
        emit BorrowerOperationsAddressChanged(_borrowerOperationsAddress);
    }

    constructor() public {
        data.maxSize = 10000000;
    }

    /*
     * @dev Set the maximum size of the list
     * @param _size Maximum size
     */
    function setMaxSize(uint256 _size) public onlyOwner {
        // New max size must be greater than old max size
        require(_size > data.maxSize);

        data.maxSize = _size;
    }

    /*
     * @dev Add a node to the list
     * @param _id Node's id
     * @param _ICR Node's ICR
     * @param _prevId Id of previous node for the insert position
     * @param _nextId Id of next node for the insert position
     */

    function insert (address _id, uint256 _ICR, uint _price, address _prevId, address _nextId) public onlyBorrowerOperations {
        _insert (_id, _ICR, _price, _prevId, _nextId);
    }

    function _insert(address _id, uint256 _ICR, uint _price, address _prevId, address _nextId) internal {
        // console.log("SortedCDPS.insert called");
        // console.log("00. gas left: %s", gasleft());
        // List must not be full
        require(!isFull());  // 1650 gas
        // console.log("01. gas left: %s", gasleft());
        // List must not already contain node
        require(!contains(_id));  // 900 gas
        // console.log("02. gas left: %s", gasleft());
        // Node id must not be null
        require(_id != address(0));  // 26 gas
        // console.log("03. gas left: %s", gasleft());
        // ICR must be non-zero
        require(_ICR > 0); // 16 gas
        // console.log("04. gas left: %s", gasleft());

        address prevId = _prevId; // 2 gas
        // console.log("05. gas left: %s", gasleft());
        address nextId = _nextId; // 3 gas
        // console.log("06. gas left: %s", gasleft());

        if (!validInsertPosition(_ICR, _price, prevId, nextId)) {
            // Sender's hint was not a valid insert position
            // Use sender's hint to find a valid insert position
            (prevId, nextId) = findInsertPosition(_ICR, _price, prevId, nextId);  // 14500k gas with 1 traversals  
        }
        // console.log("07. gas left: %s", gasleft());

         data.nodes[_id].exists = true;  // *** 21000 gas for false --> true

        // console.log("08. gas left: %s", gasleft());

        if (prevId == address(0) && nextId == address(0)) {
            // console.log("9a. gas left: %s", gasleft());
            // Insert as head and tail
            data.head = _id; // 21000 gas
            // console.log("9b. gas left: %s", gasleft());
            data.tail = _id; // 21000 gas
            // console.log("9bb. gas left: %s", gasleft());
        } else if (prevId == address(0)) { // 117 gas
            // console.log("9c. gas left: %s", gasleft());
            // Insert before `prevId` as the head
            data.nodes[_id].nextId = data.head; // 2600 gas
            // console.log("9d. gas left: %s", gasleft());
            data.nodes[data.head].prevId = _id;  // 21800 gas
            // console.log("9e. gas left: %s", gasleft());
            data.head = _id; // 5800 gas
            // console.log("9ee. gas left: %s", gasleft());
        } else if (nextId == address(0)) {
            // console.log("9f. gas left: %s", gasleft());
            // Insert after `nextId` as the tail
            data.nodes[_id].prevId = data.tail;
            // console.log("9g. gas left: %s", gasleft());
            data.nodes[data.tail].nextId = _id;
            // console.log("9h. gas left: %s", gasleft());
            data.tail = _id;
            // console.log("9i. gas left: %s", gasleft());
        } else { // 127 gas
            // console.log("9j. gas left: %s", gasleft());
            // Insert at insert position between `prevId` and `nextId`
            data.nodes[_id].nextId = nextId; // 1800 gas
            // console.log("9k. gas left: %s", gasleft());
            data.nodes[_id].prevId = prevId; // 21000 gas
            // console.log("9l. gas left: %s", gasleft());
            data.nodes[prevId].nextId = _id; // 6000 gas
            // console.log("9m. gas left: %s", gasleft());
            data.nodes[nextId].prevId = _id; // 6000 gas
            // console.log("9n. gas left: %s", gasleft());
        }

        data.size = data.size.add(1); // 5900 gas
        // ;  
        // console.log("10. gas left: %s", gasleft());
        // console.log("SortedCDPs.insert func end");
    }

    function remove(address _id) public onlyCDPManager {
        _remove(_id);
    }

    /*
     * @dev Remove a node from the list
     * @param _id Node's id
     */
    function _remove(address _id) internal {
        // List must contain the node
        // console.log("00. gas left: %s", gasleft());
        require(contains(_id)); // 940 gas
        // console.log("01. gas left: %s", gasleft());

        if (data.size > 1) { // 800 gas
            // console.log("02. gas left: %s", gasleft());
            // List contains more than a single node
            if (_id == data.head) { // 800 gas
                // console.log("03. gas left: %s", gasleft());
                // The removed node is the head
                // Set head to next node
                data.head = data.nodes[_id].nextId; // 6800 gas
                // console.log("04. gas left: %s", gasleft());
                // Set prev pointer of new head to null
                data.nodes[data.head].prevId = address(0); // 6700 gas
                // console.log("05. gas left: %s", gasleft());
            } else if (_id == data.tail) {
                // console.log("06. gas left: %s", gasleft());
                // The removed node is the tail
                // Set tail to previous node
                data.tail = data.nodes[_id].prevId;
                // console.log("07. gas left: %s", gasleft());
                // Set next pointer of new tail to null
                data.nodes[data.tail].nextId = address(0);
                // console.log("08. gas left: %s", gasleft());
            } else {
                // The removed node is neither the head nor the tail
                // Set next pointer of previous node to the next node
                // console.log("09. gas left: %s", gasleft());
                data.nodes[data.nodes[_id].prevId].nextId = data.nodes[_id].nextId; // 7600 gas
                //  console.log("10. gas left: %s", gasleft());
                // Set prev pointer of next node to the previous node
                data.nodes[data.nodes[_id].nextId].prevId = data.nodes[_id].prevId; // 7600 gas
                //  console.log("11. gas left: %s", gasleft());
            }
        } else {
            // List contains a single node
            // Set the head and tail to null
            //  console.log("12. gas left: %s", gasleft());
            data.head = address(0);
            //  console.log("13. gas left: %s", gasleft());
            data.tail = address(0);
            //  console.log("14. gas left: %s", gasleft());
        }

        delete data.nodes[_id]; // ** 11100 gas
        // console.log("15. gas left: %s", gasleft());
        data.size = data.size.sub(1); // ** 5900 gas
        // console.log("16. gas left: %s", gasleft());
    }

    /*
     * @dev Re-insert the node at a new position, based on its new ICR
     * @param _id Node's id
     * @param _newICR Node's new ICR
     * @param _prevId Id of previous node for the new insert position
     * @param _nextId Id of next node for the new insert position
     */
    function reInsert(address _id, uint256 _newICR, uint _price, address _prevId, address _nextId) public onlyBMorCDPM {
        // List must contain the node
        require(contains(_id));

        // Remove node from the list
        _remove(_id);

        if (_newICR > 0) {
            // Insert node if it has a non-zero ICR
            _insert(_id, _newICR, _price, _prevId, _nextId);
        }
    }

    /*
     * @dev Checks if the list contains a node
     * @param _transcoder Address of transcoder
     */
    function contains(address _id) public view returns (bool) {
        return data.nodes[_id].exists;
    }

    /*
     * @dev Checks if the list is full
     */
    function isFull() public view returns (bool) {
        return data.size == data.maxSize;
    }

    /*
     * @dev Checks if the list is empty
     */
    function isEmpty() public view returns (bool) {
        return data.size == 0;
    }

    /*
     * @dev Returns the current size of the list
     */
    function getSize() public view returns (uint256) {
        return data.size;
    }

    /*
     * @dev Returns the maximum size of the list
     */
    function getMaxSize() public view returns (uint256) {
        return data.maxSize;
    }

    /*
     * @dev Returns the first node in the list (node with the largest ICR)
     */
    function getFirst() public view returns (address) {
        return data.head;
    }

    /*
     * @dev Returns the last node in the list (node with the smallest ICR)
     */
    function getLast() public view returns (address) {
        return data.tail;
    }

    /*
     * @dev Returns the next node (with a smaller ICR) in the list for a given node
     * @param _id Node's id
     */
    function getNext(address _id) public view returns (address) {
        return data.nodes[_id].nextId;
    }

    /*
     * @dev Returns the previous node (with a larger ICR) in the list for a given node
     * @param _id Node's id
     */
    function getPrev(address _id) public view returns (address) {
        return data.nodes[_id].prevId;
    }

    /*
     * @dev Check if a pair of nodes is a valid insertion point for a new node with the given ICR
     * @param _ICR Node's ICR
     * @param _prevId Id of previous node for the insert position
     * @param _nextId Id of next node for the insert position
     */
    function validInsertPosition(uint256 _ICR, uint _price, address _prevId, address _nextId) public view returns (bool) {
        if (_prevId == address(0) && _nextId == address(0)) {
            // `(null, null)` is a valid insert position if the list is empty
            return isEmpty();
        } else if (_prevId == address(0)) {
            // `(null, _nextId)` is a valid insert position if `_nextId` is the head of the list
            return data.head == _nextId && _ICR >= cdpManager.getCurrentICR(_nextId, _price);
        } else if (_nextId == address(0)) {
            // `(_prevId, null)` is a valid insert position if `_prevId` is the tail of the list
            return data.tail == _prevId && _ICR <= cdpManager.getCurrentICR(_prevId, _price);
        } else {
            // `(_prevId, _nextId)` is a valid insert position if they are adjacent nodes and `_ICR` falls between the two nodes' ICRs
            return data.nodes[_prevId].nextId == _nextId && 
                   cdpManager.getCurrentICR(_prevId, _price) >= _ICR && 
                   _ICR >= cdpManager.getCurrentICR(_nextId, _price);
        }
    }

    /*
     * @dev Descend the list (larger ICRs to smaller ICRs) to find a valid insert position
     * @param _ICR Node's ICR
     * @param _startId Id of node to start ascending the list from
     */
    function descendList(uint256 _ICR, uint _price, address _startId) private view returns (address, address) {
        // If `_startId` is the head, check if the insert position is before the head
        if (data.head == _startId && _ICR >= cdpManager.getCurrentICR(_startId, _price)) {
            return (address(0), _startId);
        }

        address prevId = _startId;
        address nextId = data.nodes[prevId].nextId;

        // Descend the list until we reach the end or until we find a valid insert position
        while (prevId != address(0) && !validInsertPosition(_ICR, _price, prevId, nextId)) {
            prevId = data.nodes[prevId].nextId;
            nextId = data.nodes[prevId].nextId;
        }

        return (prevId, nextId);
    }

    /*
     * @dev Ascend the list (smaller ICRs to larger ICRs) to find a valid insert position
     * @param _ICR Node's ICR
     * @param _startId Id of node to start descending the list from
     */
    function ascendList(uint256 _ICR, uint _price, address _startId) private view returns (address, address) {
        // If `_startId` is the tail, check if the insert position is after the tail
        if (data.tail == _startId && _ICR <= cdpManager.getCurrentICR(_startId, _price)) {
            return (_startId, address(0));
        }

        address nextId = _startId;
        address prevId = data.nodes[nextId].prevId;

        // Ascend the list until we reach the end or until we find a valid insertion point
        while (nextId != address(0) && !validInsertPosition(_ICR, _price, prevId, nextId)) {
            nextId = data.nodes[nextId].prevId;
            prevId = data.nodes[nextId].prevId;
        }

        return (prevId, nextId);
    }

    /*
     * @dev Find the insert position for a new node with the given ICR
     * @param _ICR Node's ICR
     * @param _prevId Id of previous node for the insert position
     * @param _nextId Id of next node for the insert position
     */
    function findInsertPosition(uint256 _ICR, uint _price, address _prevId, address _nextId) public view returns (address, address) {
        address prevId = _prevId;
        address nextId = _nextId;

        if (prevId != address(0)) {
            if (!contains(prevId) || _ICR > cdpManager.getCurrentICR(prevId, _price)) {
                // `prevId` does not exist anymore or now has a smaller ICR than the given ICR
                prevId = address(0);
            }
        }

        if (nextId != address(0)) {
            if (!contains(nextId) || _ICR < cdpManager.getCurrentICR(nextId, _price)) {
                // `nextId` does not exist anymore or now has a larger ICR than the given ICR
                nextId = address(0);
            }
        }

        if (prevId == address(0) && nextId == address(0)) {
            // No hint - descend list starting from head
            return descendList(_ICR, _price, data.head);
        } else if (prevId == address(0)) {
            // No `prevId` for hint - ascend list starting from `nextId`
            return ascendList(_ICR, _price, nextId);
        } else if (nextId == address(0)) {
            // No `nextId` for hint - descend list starting from `prevId`
            return descendList(_ICR, _price, prevId);
        } else {
            // Descend list starting from `prevId`
            return descendList(_ICR, _price, prevId);
        }
    }
}

contracts/PoolManager.sol.content:
pragma solidity ^0.5.16;

import "./Interfaces/IBorrowerOperations.sol";
import './Interfaces/IPool.sol';
import './Interfaces/IPoolManager.sol';
import './Interfaces/ICDPManager.sol';
import './Interfaces/IStabilityPool.sol';
import './Interfaces/IPriceFeed.sol';
import './Interfaces/ICLVToken.sol';
import './DeciMath.sol';
import "./Dependencies/SafeMath.sol";
import "./Dependencies/Ownable.sol";
import "./Dependencies/console.sol";

// PoolManager maintains all pools 
contract PoolManager is Ownable, IPoolManager {
    using SafeMath for uint;

    // --- Events ---

    event BorrowerOperationsAddressChanged(address _newBorrowerOperationsAddress);
    event CDPManagerAddressChanged(address _newCDPManagerAddress);
    event PriceFeedAddressChanged(address _newPriceFeedAddress);
    event CLVTokenAddressChanged(address _newCLVTokenAddress);
    event StabilityPoolAddressChanged(address _newStabilityPoolAddress);
    event ActivePoolAddressChanged(address _newActivePoolAddress);
    event DefaultPoolAddressChanged(address _newDefaultPoolAddress);

    event UserSnapshotUpdated(uint _P, uint _S);
    event P_Updated(uint _P);
    event S_Updated(uint _S);
    event UserDepositChanged(address indexed _user, uint _amount);
    event ETHGainWithdrawn(address indexed _user, uint _ETH);
    event ETHGainWithdrawnToCDP(address indexed _CDPOwner, uint _ETH);

    // --- Connected contract declarations ---

    IBorrowerOperations borrowerOperations;
    address public borrowerOperationsAddress;

    address public cdpManagerAddress;
    ICDPManager cdpManager = ICDPManager(cdpManagerAddress);

    IPriceFeed priceFeed;
    address public priceFeedAddress;

    ICLVToken CLV;
    address public clvAddress;

    IStabilityPool public stabilityPool;
    address public stabilityPoolAddress;

    IPool public activePool;
    address public activePoolAddress;

    IPool public defaultPool;
    address public defaultPoolAddress;

   // --- Data structures ---

    mapping (address => uint) public initialDeposits;

    struct Snapshot {
        uint S;
        uint P;
        uint scale;
        uint epoch;
    }

    /* P: Running product by which to multiply an initial deposit, in order to find the current compounded deposit, 
    given a series of liquidations, each of which cancel some CLV debt with the deposit. 

    During its lifetime, a deposit's value evolves from d0 to (d0 * P / P(0) ), where P(0) 
    is the snapshot of P taken at the instant the deposit was made. 18 DP decimal.  */
    uint public P = 1e18;

    uint public currentScale;  // Each time the scale of P shifts by 1e18, the scale is incremented by 1

    uint public currentEpoch;  // With each offset that fully empties the Pool, the epoch is incremented by 1

    /* S: During it's lifetime, each deposit d0 earns an ETH gain of ( d0 * [S - S(0)] )/P(0), where S(0) 
    is the snapshot of S taken at the instant the deposit was made.

    The 'S' sums are stored in a nested mapping (epoch => scale => sum):

    - The inner mapping records the sum S at different scales
    - The outer mapping records the (scale => sum) mappings, for different epochs. */
    mapping (uint => mapping(uint => uint)) public epochToScaleToSum;

    // Map users to their individual snapshot structs
    mapping (address => Snapshot) public snapshot;

    // Error trackers for the error correction in the offset calculation
    uint lastETHError_Offset;
    uint lastCLVLossError_Offset;

    // --- Modifiers ---

    modifier onlyCDPManager() {
        require(_msgSender() == cdpManagerAddress, "PoolManager: Caller is not the CDPManager");
        _;
    }

     modifier onlyBorrowerOperations() {
        require(_msgSender() == borrowerOperationsAddress, "PoolManager: Caller is not the BorrowerOperations contract");
        _;
    }

    modifier onlyStabilityPoolorActivePool {
        require(
            _msgSender() == stabilityPoolAddress ||  _msgSender() ==  activePoolAddress, 
            "PoolManager: Caller is neither StabilityPool nor ActivePool");
        _;
    }

    constructor() public {}

    // --- Dependency setters ---

    function setBorrowerOperations(address _borrowerOperationsAddress) public onlyOwner {
        borrowerOperationsAddress = _borrowerOperationsAddress;
        borrowerOperations = IBorrowerOperations(_borrowerOperationsAddress);
        emit BorrowerOperationsAddressChanged(_borrowerOperationsAddress);
    }

    function setCDPManagerAddress(address _cdpManagerAddress) public onlyOwner {
        cdpManagerAddress = _cdpManagerAddress;
        cdpManager = ICDPManager(_cdpManagerAddress);
        emit CDPManagerAddressChanged(_cdpManagerAddress);
    }

     function setPriceFeed(address _priceFeedAddress) public onlyOwner {
        priceFeedAddress = _priceFeedAddress;
        priceFeed = IPriceFeed(_priceFeedAddress);
        emit PriceFeedAddressChanged(_priceFeedAddress);
    }

    function setCLVToken(address _CLVAddress) public onlyOwner {
        clvAddress = _CLVAddress;
        CLV = ICLVToken(_CLVAddress);
        emit CLVTokenAddressChanged(_CLVAddress);
    }

    function setStabilityPool(address _stabilityPoolAddress) public onlyOwner {
        stabilityPoolAddress = _stabilityPoolAddress;
        stabilityPool = IStabilityPool(stabilityPoolAddress);
        emit StabilityPoolAddressChanged(_stabilityPoolAddress);
    }

    function setActivePool(address _activePoolAddress) public onlyOwner {
        activePoolAddress = _activePoolAddress;
        activePool = IPool(activePoolAddress);
        emit ActivePoolAddressChanged(_activePoolAddress);
    }

    function setDefaultPool(address _defaultPoolAddress) public onlyOwner {
        defaultPoolAddress = _defaultPoolAddress;
        defaultPool = IPool(defaultPoolAddress);
        emit DefaultPoolAddressChanged(_defaultPoolAddress);
    }

    // --- Getters ---

    // Return the current ETH balance of the PoolManager contract
    function getBalance() public view returns(uint) {
        return address(this).balance;
    } 

    // Return the total collateral ratio (TCR) of the system, based on the most recent ETH:USD price
    function getTCR() public view returns (uint) {
        uint price = priceFeed.getPrice();

        uint activeColl = activePool.getETH();
        uint activeDebt = activePool.getCLV();
        uint liquidatedColl = defaultPool.getETH();
        uint closedDebt = defaultPool.getCLV();

        uint totalCollateral = activeColl.add(liquidatedColl);
        uint totalDebt = activeDebt.add(closedDebt); 

        // Handle edge cases of div-by-0
        if(totalCollateral == 0 && totalDebt == 0 ) {
            return 1;
        }  else if (totalCollateral != 0 && totalDebt == 0 ) {
            return 2**256 - 1; // TCR is technically infinite
        }

        // Calculate TCR
        uint TCR = totalCollateral.mul(price).div(totalDebt);
        return TCR;
    }

    // Return the total active debt (in CLV) in the system
    function getActiveDebt() public view returns (uint) {
        return activePool.getCLV();
    }    

    // Return the total active collateral (in ETH) in the system
    function getActiveColl() public view returns (uint) {
        return activePool.getETH();
    } 

    // Return the amount of closed debt (in CLV)
    function getClosedDebt() public view returns (uint) {
        return defaultPool.getCLV();
    }    

    // Return the amount of closed collateral (in ETH)
    function getLiquidatedColl() public view returns (uint) {
        return defaultPool.getETH();
    }

    // Return the total CLV in the Stability Pool
    function getStabilityPoolCLV() public view returns (uint) {
        return stabilityPool.getCLV();
    }

    // Add the received ETH to the total active collateral
    function addColl() public payable onlyBorrowerOperations returns (bool) {
        // Send ETH to Active Pool and increase its recorded ETH balance
       (bool success, ) = activePoolAddress.call.value(msg.value)("");
       assert(success == true);
       return success;
    }

    // Transfer the specified amount of ETH to _account and updates the total active collateral
    function withdrawColl(address _account, uint _ETH) public onlyBorrowerOperations returns (bool) {
        activePool.sendETH(_account, _ETH);
        return true;
    }

    // Issue the specified amount of CLV to _account and increases the total active debt
    function withdrawCLV(address _account, uint _CLV) public onlyBorrowerOperations returns (bool) {
        activePool.increaseCLV(_CLV);  // 9500
        CLV.mint(_account, _CLV);  // 37500

        return true;
    }

    // Burn the specified amount of CLV from _account and decreases the total active debt
    function repayCLV(address _account, uint _CLV) public onlyBorrowerOperations returns (bool) {
        activePool.decreaseCLV(_CLV);
        CLV.burn(_account, _CLV);
        return true;
    }           

    // Update the Active Pool and the Default Pool when a CDP gets closed
    function liquidate(uint _CLV, uint _ETH) public onlyCDPManager returns (bool) {
        // Transfer the debt & coll from the Active Pool to the Default Pool
        defaultPool.increaseCLV(_CLV);
        activePool.decreaseCLV(_CLV);
        activePool.sendETH(defaultPoolAddress, _ETH);

        return true;
    }

    // Move a CDP's pending debt and collateral rewards from distributions, from the Default Pool to the Active Pool
    function moveDistributionRewardsToActivePool(uint _CLV, uint _ETH) public onlyCDPManager returns (bool) {
        // Transfer the debt & coll from the Default Pool to the Active Pool
        defaultPool.decreaseCLV(_CLV);  
        activePool.increaseCLV(_CLV); 
        defaultPool.sendETH(activePoolAddress, _ETH); 

        return true;
    }

    // Burn the received CLV, transfers the redeemed ETH to _account and updates the Active Pool
    function redeemCollateral(address _account, uint _CLV, uint _ETH) public onlyCDPManager returns (bool) {
        // Update Active Pool CLV, and send ETH to account
        activePool.decreaseCLV(_CLV);  
        activePool.sendETH(_account, _ETH); 

        CLV.burn(_account, _CLV); 
        return true;
    }

    /* Return the ETH gain earned by the deposit. Given by the formula:  E = d0 * (S - S(0))/P(0)
    where S(0), P(0) are the depositor's snapshots of the sum S and product P, respectively. */
    function getCurrentETHGain(address _user) public view returns(uint) {
        uint initialDeposit = initialDeposits[_user];

        if (initialDeposit == 0) { return 0; }

        uint snapshot_S = snapshot[_user].S;  
        uint snapshot_P = snapshot[_user].P;
        uint scaleSnapshot = snapshot[_user].scale;
        uint epochSnapshot = snapshot[_user].epoch;

        uint ETHGain;

        /* Grab the reward sum from the epoch at which the deposit was made. The reward may span up to 
        one scale change.  
        If it does, the second portion of the reward is scaled by 1e18. 
        If the reward spans no scale change, the second portion will be 0. */
        uint firstPortion = epochToScaleToSum[epochSnapshot][scaleSnapshot].sub(snapshot_S);
        uint secondPortion = epochToScaleToSum[epochSnapshot][scaleSnapshot.add(1)].div(1e18);

        ETHGain = initialDeposit.mul(firstPortion.add(secondPortion)).div(snapshot_P).div(1e18);

        return ETHGain;
    }

    /* Return the user's compounded deposit.  Given by the formula:  d = d0 * P/P(0)
    where P_(0) is the depositor's snapshot of the product P. */
    function getCompoundedCLVDeposit(address _user) public view returns(uint) {
        uint userDeposit = initialDeposits[_user];

        if (userDeposit == 0) { return 0; }

        uint snapshot_P = snapshot[_user].P; 
        uint scaleSnapshot = snapshot[_user].scale;
        uint epochSnapshot = snapshot[_user].epoch;

        // If deposit was made before a pool-emptying event, then it has been fully cancelled with debt -- so, return 0
        if (epochSnapshot < currentEpoch) { return 0; }

        uint compoundedDeposit;
        uint scaleDiff = currentScale.sub(scaleSnapshot);

        /* Compute the compounded deposit. If a scale change in P was made during the deposit's lifetime, 
        account for it.  If more than one scale change was made, then the deposit has decreased by a factor of 
        at least 1e-18 -- so return 0.*/
        if (scaleDiff == 0) { 
            compoundedDeposit = userDeposit.mul(P).div(snapshot_P);
        } else if (scaleDiff == 1) {
            compoundedDeposit = userDeposit.mul(P).div(snapshot_P).div(1e18);
        } else {
            compoundedDeposit = 0;
        }

        // If compounded deposit is less than a billionth of the initial deposit, return 0
        if (compoundedDeposit < userDeposit.div(1e9)) { return 0; }

        return compoundedDeposit;
    }

    // --- Internal Stability Pool functions --- 

    // Deposit _amount CLV from _address, to the Stability Pool.
    function depositCLV(address _address, uint _amount) internal returns(bool) {
        require(initialDeposits[_address] == 0, "PoolManager: user already has a StabilityPool deposit");

        // Transfer the CLV tokens from the user to the Stability Pool's address, and update its recorded CLV
        CLV.sendToPool(_address, stabilityPoolAddress, _amount);
        stabilityPool.increaseCLV(_amount);

        // Record the deposit made by user
        initialDeposits[_address] = _amount;

        // Record new individual snapshots of the running product P and sum S for the user
        snapshot[_address].P = P;
        snapshot[_address].S = epochToScaleToSum[currentEpoch][currentScale];
        snapshot[_address].scale = currentScale;
        snapshot[_address].epoch = currentEpoch;

        emit UserSnapshotUpdated(snapshot[_address].P, snapshot[_address].S);
        emit UserDepositChanged(_address, _amount);
        return true;
    }

   // Transfers _address's compounded deposit and ETH gain, to _address.
    function retrieveToUser(address _address) internal returns(uint compoundedCLVDeposit, uint ETHGain) {
        uint userDeposit = initialDeposits[_address];

        ETHGain = getCurrentETHGain(_address);
        compoundedCLVDeposit = getCompoundedCLVDeposit(_address);
        initialDeposits[_address] = 0;

        emit UserDepositChanged(_address, 0);

        // Send CLV to user and decrease CLV in Pool
        CLV.returnFromPool(stabilityPoolAddress, _address, DeciMath.getMin(compoundedCLVDeposit, stabilityPool.getCLV()));

        stabilityPool.decreaseCLV(compoundedCLVDeposit);

        // Send ETH to user
        stabilityPool.sendETH(_address, ETHGain);
        emit ETHGainWithdrawn(_address, ETHGain);

        return (compoundedCLVDeposit, ETHGain);
    }

    // Transfer _address's compounded deposit to _address, and their ETH gain to their CDP.
    function retrieveToCDP(address _address, address _hint) internal returns(uint compoundedCLVDeposit, uint ETHGain) {
        uint userDeposit = initialDeposits[_address];  
        require(userDeposit > 0, 'PoolManager: User must have a non-zero deposit');  

        ETHGain = getCurrentETHGain(_address);
        compoundedCLVDeposit = getCompoundedCLVDeposit(_address);

        initialDeposits[_address] = 0; 

        emit UserDepositChanged(_address, 0); 

        // Send CLV to user and decrease CLV in StabilityPool
        CLV.returnFromPool(stabilityPoolAddress, _address, DeciMath.getMin(compoundedCLVDeposit, stabilityPool.getCLV()));

        stabilityPool.decreaseCLV(compoundedCLVDeposit);

        // Pull ETHShare from StabilityPool, and send to CDP
        stabilityPool.sendETH(address(this), ETHGain); 
        borrowerOperations.addColl.value(ETHGain)(_address, _hint); 
        emit ETHGainWithdrawnToCDP(_address, ETHGain);

        return (compoundedCLVDeposit, ETHGain);
    }

    // --- External StabilityPool Functions ---

    /* Send ETHGain to user's address, and updates their deposit, 
    setting newDeposit = compounded deposit + amount. */
    function provideToSP(uint _amount) external returns(bool) {
        address user = _msgSender();

        // If user has no deposit, make one with _amount
        if (initialDeposits[user] == 0) {
            depositCLV(user, _amount);
            return true;
        }

        /* If user already has a deposit, retrieve their ETH gain and current deposit,
         then make a new composite deposit */
        (uint returnedCLV, ) = retrieveToUser(user);

        uint newDeposit = returnedCLV + _amount;
        depositCLV(user, newDeposit);

        return true;
    }

    /* Withdraw _amount of CLV and the callers entire ETH gain from the 
    Stability Pool, and updates the callers reduced deposit. 

    If  _amount is 0, the user only withdraws their ETH gain, no CLV.
    If _amount > userDeposit, the user withdraws all their ETH gain, and all of their compounded deposit.

    In all cases, the entire ETH gain is sent to user. */
    function withdrawFromSP(uint _amount) external returns(bool) {
        address user = _msgSender();

        // Retrieve CLV and ETH for the user
        (uint returnedCLV, ) = retrieveToUser(user);

        // If requested withdrawal amount is less than available CLV, re-deposit the difference.
        if (_amount < returnedCLV) {
            depositCLV(user, returnedCLV.sub(_amount));
        }

        return true;
    }

    /* Transfer the callers entire ETH gain from the Stability Pool to the callers CDP, and leaves
    their compounded deposit in the Stability Pool. */
    function withdrawFromSPtoCDP(address _user, address _hint) external returns(bool) {
        uint userDeposit = initialDeposits[_user]; 

        if (userDeposit == 0) { return false; } 

        // Retrieve all CLV to user's CLV balance, and ETH to their CDP
        (uint returnedCLV, ) = retrieveToCDP(_user, _hint); 

        depositCLV(_user, returnedCLV); 
        return true;
    }

     /* Cancel out the specified _debt against the CLV contained in the Stability Pool (as far as possible)  
    and transfers the CDP's ETH collateral from ActivePool to StabilityPool. 
    Returns the amount of debt that could not be cancelled, and the corresponding ether.
    Only callable from close() and closeCDPs() functions in CDPManager */
  function offset(uint _debt, uint _coll,  uint CLVInPool) 
    external 
    payable 
    onlyCDPManager 
    returns (uint debtRemainder, uint collRemainder)  {    
        uint totalCLVDeposits = stabilityPool.getCLV(); 

        // If the debt is larger than the deposited CLV, offset an amount of debt corresponding to the latter
        uint debtToOffset = DeciMath.getMin(_debt, CLVInPool);  

        // Collateral to be added in proportion to the debt that is cancelled 
        uint collToAdd = _coll.mul(debtToOffset).div(_debt);

        (uint ETHGainPerUnitStaked,
         uint CLVLossPerUnitStaked) = computeRewardsPerUnitStaked(collToAdd, debtToOffset, totalCLVDeposits);

        updateRewardSumAndProduct(ETHGainPerUnitStaked, CLVLossPerUnitStaked);

        moveOffsetCollAndDebt(collToAdd, debtToOffset);

        // Return the amount of debt & coll that could not be offset against the Stability Pool due to insufficiency
        debtRemainder = _debt.sub(debtToOffset);
        collRemainder = _coll.sub(collToAdd);

        return (debtRemainder, collRemainder);
    }

    // --- Offset helper functions ---

    function computeRewardsPerUnitStaked(uint collToAdd, uint debtToOffset, uint totalCLVDeposits) 
    internal 
    returns(uint ETHGainPerUnitStaked, uint CLVLossPerUnitStaked) 
    {
        uint CLVLossNumerator = debtToOffset.mul(1e18).sub(lastCLVLossError_Offset);
        uint ETHNumerator = collToAdd.mul(1e18).add(lastETHError_Offset);

        // Compute the CLV and ETH rewards, and error corrections
        uint CLVLossPerUnitStaked;

        if (debtToOffset >= totalCLVDeposits) {
            CLVLossPerUnitStaked = 1e18;
            lastCLVLossError_Offset = 0;
        } else {
            CLVLossPerUnitStaked = (CLVLossNumerator.div(totalCLVDeposits)).add(1); // add 1 to make error in quotient positive
             lastCLVLossError_Offset = (CLVLossPerUnitStaked.mul(totalCLVDeposits)).sub(CLVLossNumerator);
        } 

        uint ETHGainPerUnitStaked = ETHNumerator.div(totalCLVDeposits); 
        lastETHError_Offset = ETHNumerator.sub(ETHGainPerUnitStaked.mul(totalCLVDeposits)); 

        return (ETHGainPerUnitStaked, CLVLossPerUnitStaked);
    }

    // Update the Stability Pool reward sum S and product P
    function updateRewardSumAndProduct(uint ETHGainPerUnitStaked, uint CLVLossPerUnitStaked) internal {
         // Make product factor 0 if there was a pool-emptying. Otherwise, it is (1 - CLVLossPerUnitStaked)
        uint newProductFactor = CLVLossPerUnitStaked >= 1e18 ? 0 : uint(1e18).sub(CLVLossPerUnitStaked);

        // Update the ETH reward sum at the current scale
        uint marginalETHGain = ETHGainPerUnitStaked.mul(P);
        epochToScaleToSum[currentEpoch][currentScale] = epochToScaleToSum[currentEpoch][currentScale].add(marginalETHGain);
        emit S_Updated(epochToScaleToSum[currentEpoch][currentScale]); 

       // If the pool was emptied, increment the epoch and reset the scale and product P
        if (newProductFactor == 0) {
            currentEpoch = currentEpoch.add(1);
            currentScale = 0;
            P = 1e18;

        // If multiplying P by a non-zero product factor would round P to zero, increment the scale 
        } else if (P.mul(newProductFactor) < 1e18) {
            P = P.mul(newProductFactor);
            currentScale = currentScale.add(1);
         } else {
            P = P.mul(newProductFactor).div(1e18); 
        }

        emit P_Updated(P); 
    }

    function moveOffsetCollAndDebt(uint collToAdd, uint debtToOffset) internal {
        // Cancel the liquidated CLV debt with the CLV in the stability pool
        activePool.decreaseCLV(debtToOffset);  
        stabilityPool.decreaseCLV(debtToOffset); 

        // Send ETH from Active Pool to Stability Pool
        activePool.sendETH(stabilityPoolAddress, collToAdd);  

        // Burn the debt that was successfully offset
        CLV.burn(stabilityPoolAddress, debtToOffset); 
    }

    function () external payable onlyStabilityPoolorActivePool {}
}

contracts/PriceFeed.sol.content:
pragma solidity ^0.5.16;


import "./Interfaces/ICDPManager.sol";
import "./Interfaces/IPriceFeed.sol";
import "./Interfaces/IDeployedAggregator.sol";
import "./Interfaces/AggregatorInterface.sol";
import "./Dependencies/SafeMath.sol";
import "./Dependencies/Ownable.sol";
import "./Dependencies/console.sol";

contract PriceFeed is Ownable, IPriceFeed {
    using SafeMath for uint256;

    uint256 constant DIGITS = 1e18;
    uint256 public price = 200 * DIGITS;

    address public cdpManagerAddress;
    address public poolManagerAddress;

    ICDPManager cdpManager;

    // Mainnet Chainlink aggregator
    address public priceAggregatorAddress;
    IDeployedAggregator priceAggregator;

    // Testnet Chainlink aggregator
    address public priceAggregatorAddress_Testnet;
    AggregatorInterface priceAggregator_Testnet;

    event PriceUpdated(uint256 _newPrice);
    event CDPManagerAddressChanged(address _cdpManagerAddress);
    event PoolManagerAddressChanged(address _poolManagerAddress);

    // --- Dependency setters ---

    function setCDPManagerAddress(address _cdpManagerAddress) public onlyOwner {
        cdpManagerAddress = _cdpManagerAddress;
        cdpManager = ICDPManager(_cdpManagerAddress);
        emit CDPManagerAddressChanged(_cdpManagerAddress);
    }

    function setPoolManagerAddress(address _poolManagerAddress) public onlyOwner {
        poolManagerAddress = _poolManagerAddress;
        emit PoolManagerAddressChanged(_poolManagerAddress);
    }

    // Mainnet Chainlink address setter
    function setAggregator(address _priceAggregatorAddress) public onlyOwner {
        priceAggregatorAddress = _priceAggregatorAddress;
        priceAggregator = IDeployedAggregator(_priceAggregatorAddress);
    }

    // Testnet Chainlink address setter
    function setAggregator_Testnet(address _priceAggregatorAddress) public onlyOwner {
        priceAggregator_Testnet = AggregatorInterface(_priceAggregatorAddress);
    }

    // --- Modifiers ---

    modifier onlyCDPManagerOrPoolManager {
        require(_msgSender() == cdpManagerAddress ||_msgSender() == poolManagerAddress,
            "PriceFeed: Caller is neither CDPManager nor PoolManager"
        );
        _;
    }

    // --- Functions ---

    function getPrice() public view returns (uint256) {
        return price;
    }

    // --- DEVELOPMENT FUNCTIONALITY  ---

    /* Manual public price setter. 
    TODO: remove before mainnet deployment. */
    function setPrice(uint256 _price) public returns (bool) {
        price = _price;
        emit PriceUpdated(price);
        return true;
    }

    // --- MAINNET FUNCTIONALITY ---

    // TODO: convert received Chainlink price to precision-18 before setting state variable
    function updatePrice() public onlyCDPManagerOrPoolManager returns (uint256) {
        price = getLatestPrice();
        emit PriceUpdated(price);
        return price;
    }

    function getLatestPrice() public view returns (uint256) {
        int256 intPrice = priceAggregator.currentAnswer();
        require(intPrice >= 0, "Price response from aggregator is negative int");

        return uint256(intPrice);
    }

    function getLatestAnswerID() public view returns (uint256) {
        return priceAggregator.latestCompletedAnswer();
    }

    // Get the block timestamp at which the reference price was last updated
    function getLatestTimestamp() public view returns (uint256) {
        return priceAggregator.updatedHeight();
    }

    // ---- ROPSTEN FUNCTIONALITY - TODO: Remove before Mainnet deployment ----

    function updatePrice_Testnet() public returns (uint256) {
        price = getLatestPrice_Testnet();
        emit PriceUpdated(price);
        return price;
    }

    function getLatestPrice_Testnet() public view returns (uint256) {
        int256 intPrice = priceAggregator_Testnet.latestAnswer();
        require( intPrice >= 0, "Price response from aggregator is negative int");

        return uint256(intPrice).mul(10000000000);
    }

    // Get the block timestamp at which the reference data was last updated
    function getLatestTimestamp_Testnet() public view returns (uint256) {
        uint256 latestTimestamp = priceAggregator_Testnet.latestTimestamp();

        return latestTimestamp;
    }

    // Get the past price from 'n' rounds ago
    function getPreviousPrice_Testnet(uint256 _n) public view returns (uint256) {
        uint256 latestAnswerID = priceAggregator_Testnet.latestRound();
        require(_n <= latestAnswerID, "Not enough history");

        int256 prevPrice = priceAggregator_Testnet.getAnswer(latestAnswerID - _n);
        require(prevPrice >= 0, "Price response from aggregator is negative int");

        return uint256(prevPrice).mul(10000000000);
    }

    // Get the block timestamp from the round that occurred 'n' rounds ago
    function getPreviousTimestamp_Testnet(uint256 _n) public view returns (uint256) {
        uint256 latestAnswerID = priceAggregator_Testnet.latestRound();
        require(_n <= latestAnswerID, "Not enough history");

        return priceAggregator_Testnet.getTimestamp(latestAnswerID - _n);
    }
}

contracts/StabilityPool.sol.content:
pragma solidity ^0.5.16;

import './Interfaces/IStabilityPool.sol';
import "./Dependencies/SafeMath.sol";
import "./Dependencies/Ownable.sol";
import "./Dependencies/console.sol";

contract StabilityPool is Ownable, IStabilityPool {
    using SafeMath for uint256;

    address public poolManagerAddress;
    address public defaultPoolAddress;
    address public activePoolAddress;
    uint256 public ETH;  // deposited ether tracker

    // Total CLV held in the pool. Changes when users deposit/withdraw, and when CDP debt is offset.
    uint256 public totalCLVDeposits; 

    constructor() public {}

    // --- Contract setters ---

    function setPoolManagerAddress(address _poolManagerAddress) public onlyOwner {
        poolManagerAddress = _poolManagerAddress;
        emit PoolManagerAddressChanged(poolManagerAddress);
    }

    function setActivePoolAddress(address _activePoolAddress) public onlyOwner {
        activePoolAddress = _activePoolAddress;
        emit ActivePoolAddressChanged(activePoolAddress);
    }

    function setDefaultPoolAddress(address _defaultPoolAddress) public onlyOwner {
        defaultPoolAddress = _defaultPoolAddress; 
        emit DefaultPoolAddressChanged(defaultPoolAddress);
    }

    // --- Getters for public variables. Required by IPool interface ---

    function getETH() public view returns(uint) {
        return ETH;
    }

    function getCLV() public view returns(uint) {
        return totalCLVDeposits;
    }

    // --- Pool functionality ---

    function sendETH(address _account, uint _amount) public onlyPoolManager returns(bool){
        ETH = ETH.sub(_amount);
        (bool success, ) = _account.call.value(_amount)("");  // use call.value()('') as per Consensys latest advice 
        assert(success == true);

        emit ETHBalanceUpdated(ETH);
        emit EtherSent(_account, _amount);
        return success;
    }

    function increaseCLV(uint _amount) public onlyPoolManager () {
        totalCLVDeposits  = totalCLVDeposits.add(_amount);
        emit CLVBalanceUpdated(totalCLVDeposits);
    }

    function decreaseCLV(uint _amount) public onlyPoolManager () {
        totalCLVDeposits = totalCLVDeposits.sub(_amount);
        emit CLVBalanceUpdated(totalCLVDeposits);
    }

    /* Returns the raw ether balance at StabilityPool address.  
    Not necessarily equal to the ETH state variable - ether can be forcibly sent to contracts. */
    function getRawETHBalance() public view returns(uint) {
        return address(this).balance;
    }

    modifier onlyPoolManager {
        require(_msgSender() == poolManagerAddress, "StabilityPool:  Caller is not the PoolManager");
        _;
    }

    modifier onlyPoolManagerOrPool {
        require(
            _msgSender() == poolManagerAddress || 
            _msgSender() == activePoolAddress || 
            _msgSender() == defaultPoolAddress, 
            "StabilityPool: Caller is neither the PoolManager nor a Pool");
        _;
    }

    function () external payable onlyPoolManagerOrPool {
        ETH = ETH.add(msg.value);
    }
}
