contracts/ActivePool.sol.content:
pragma solidity 0.5.16;

import './Interfaces/IPool.sol';
import "./Dependencies/SafeMath.sol";
import "./Dependencies/Ownable.sol";
import "./Dependencies/console.sol";

contract ActivePool is Ownable, IPool {
    using SafeMath for uint256;

    address public poolManagerAddress;
    address public troveManagerAddress;
    address public defaultPoolAddress;
    uint256 internal ETH;  // deposited ether tracker
    uint256 internal CLVDebt;

    // --- Events ---

    event TroveManagerAddressChanged(address _troveManagerAddress);

    // --- Modifiers ---

    modifier onlyPoolManager {
        require(_msgSender() == poolManagerAddress, "ActivePool: Caller is not the PoolManager");
        _;
    }

    modifier onlyPoolManagerOrDefaultPool {
        require(
            _msgSender() == poolManagerAddress || 
            _msgSender() == defaultPoolAddress,
            "ActivePool: Caller is neither the PoolManager nor Default Pool");
        _;
    }

    modifier onlyPoolManagerOrTroveManager {
        require(
            _msgSender() == poolManagerAddress || 
            _msgSender() == troveManagerAddress, 
            "ActivePool: Caller is neither the PoolManager nor TroveManager");
        _;
    }

    // --- Contract setters ---

    function setAddresses(
        address _poolManagerAddress,
        address _troveManagerAddress,
        address _defaultPoolAddress
    )
        external
        onlyOwner
    {
        poolManagerAddress = _poolManagerAddress;
        troveManagerAddress = _troveManagerAddress;
        defaultPoolAddress = _defaultPoolAddress;

        emit PoolManagerAddressChanged(_poolManagerAddress);
        emit TroveManagerAddressChanged(_troveManagerAddress);
        emit DefaultPoolAddressChanged(_defaultPoolAddress);

        _renounceOwnership();
    }

    // --- Getters for public variables. Required by IPool interface ---

    function getETH() external view returns (uint) {
        return ETH;
    }

    function getCLVDebt() external view returns (uint) {
        return CLVDebt;
    }

    // --- Pool functionality ---

    function sendETH(address _account, uint _amount) public onlyPoolManagerOrTroveManager {
        ETH = ETH.sub(_amount);  
        emit EtherSent(_account, _amount);  

        (bool success, ) = _account.call.value(_amount)(""); //  use call.value()('') as per Consensys latest advice 
        require(success, "ActivePool: sending ETH failed");
    }

    function increaseCLVDebt(uint _amount) external onlyPoolManager () {
        CLVDebt  = CLVDebt.add(_amount); 
    }

    function decreaseCLVDebt(uint _amount) external onlyPoolManager () {
        CLVDebt = CLVDebt.sub(_amount); 
    }

    /* Returns the raw ether balance at ActivePool address.  
    Not necessarily equal to the ETH state variable - ether can be forcibly sent to contracts. */
    function getRawETHBalance() external view returns (uint) {
        return address(this).balance;
    }

    function () external payable onlyPoolManagerOrDefaultPool {
        ETH = ETH.add(msg.value);
    }
}

contracts/Interfaces/IPool.sol.content:
pragma solidity >=0.5.16;

// Common interface for the Pools.
interface IPool {
    // --- Events ---
    event ETHBalanceUpdated(uint _newBalance);

    event CLVBalanceUpdated(uint _newBalance);

    event PoolManagerAddressChanged(address _newAddress);

    event ActivePoolAddressChanged(address _newActivePoolAddress);

    event DefaultPoolAddressChanged(address _newDefaultPoolAddress);

    event StabilityPoolAddressChanged(address _newStabilityPoolAddress);

    event EtherSent(address _to, uint _amount);

    // --- Functions ---
    function getETH() external view returns (uint);

    function getCLVDebt() external view returns (uint);

    function sendETH(address _account, uint _amount) external;

    function increaseCLVDebt(uint _amount) external;

    function decreaseCLVDebt(uint _amount) external;

    function getRawETHBalance() external view returns (uint);
}

