pragma solidity 0.5.16;

contract Migrations {
  address public owner;
  uint public last_completed_migration;

  modifier restricted() {
    if (msg.sender == owner) _;
  }

  constructor() public {
    owner = msg.sender;
  }

  function setCompleted(uint completed) public restricted {
    last_completed_migration = completed;
  }

  function upgrade(address new_address) public restricted {
    Migrations upgraded = Migrations(new_address);
    upgraded.setCompleted(last_completed_migration);
  }
}

contracts/MultiCDPGetter.sol.content:
pragma solidity 0.5.16;
pragma experimental ABIEncoderV2;

import "./TroveManager.sol";
import "./SortedCDPs.sol";

/*  Helper contract for grabbing CDP data for the front end. Not part of the core Liquity system. */
contract MultiCDPGetter {
    struct CombinedCDPData {
        address owner;

        uint debt;
        uint coll;
        uint stake;

        uint snapshotETH;
        uint snapshotCLVDebt;
    }

    TroveManager public troveManager; // XXX CDPs missing from ITroveManager?
    ISortedCDPs public sortedCDPs;

    constructor(TroveManager _troveManager, ISortedCDPs _sortedCDPs) public {
        troveManager = _troveManager;
        sortedCDPs = _sortedCDPs;
    }

    function getMultipleSortedCDPs(int _startIdx, uint _count)
        external view returns (CombinedCDPData[] memory _cdps)
    {
        uint startIdx;
        bool descend;

        if (_startIdx >= 0) {
            startIdx = uint(_startIdx);
            descend = true;
        } else {
            startIdx = uint(-(_startIdx + 1));
            descend = false;
        }

        uint sortedCDPsSize = sortedCDPs.getSize();

        if (startIdx >= sortedCDPsSize) {
            _cdps = new CombinedCDPData[](0);
        } else {
            uint maxCount = sortedCDPsSize - startIdx;

            if (_count > maxCount) {
                _count = maxCount;
            }

            if (descend) {
                _cdps = _getMultipleSortedCDPsFromHead(startIdx, _count);
            } else {
                _cdps = _getMultipleSortedCDPsFromTail(startIdx, _count);
            }
        }
    }

    function _getMultipleSortedCDPsFromHead(uint _startIdx, uint _count)
        internal view returns (CombinedCDPData[] memory _cdps)
    {
        address currentCDPowner = sortedCDPs.getFirst();

        for (uint idx = 0; idx < _startIdx; ++idx) {
            currentCDPowner = sortedCDPs.getNext(currentCDPowner);
        }

        _cdps = new CombinedCDPData[](_count);

        for (uint idx = 0; idx < _count; ++idx) {
            _cdps[idx].owner = currentCDPowner;
            (
                _cdps[idx].debt,
                _cdps[idx].coll,
                _cdps[idx].stake,
                /* status */,
                /* arrayIndex */
            ) = troveManager.CDPs(currentCDPowner);
            (
                _cdps[idx].snapshotETH,
                _cdps[idx].snapshotCLVDebt
            ) = troveManager.rewardSnapshots(currentCDPowner);

            currentCDPowner = sortedCDPs.getNext(currentCDPowner);
        }
    }

    function _getMultipleSortedCDPsFromTail(uint _startIdx, uint _count)
        internal view returns (CombinedCDPData[] memory _cdps)
    {
        address currentCDPowner = sortedCDPs.getLast();

        for (uint idx = 0; idx < _startIdx; ++idx) {
            currentCDPowner = sortedCDPs.getPrev(currentCDPowner);
        }

        _cdps = new CombinedCDPData[](_count);

        for (uint idx = 0; idx < _count; ++idx) {
            _cdps[idx].owner = currentCDPowner;
            (
                _cdps[idx].debt,
                _cdps[idx].coll,
                _cdps[idx].stake,
                /* status */,
                /* arrayIndex */
            ) = troveManager.CDPs(currentCDPowner);
            (
                _cdps[idx].snapshotETH,
                _cdps[idx].snapshotCLVDebt
            ) = troveManager.rewardSnapshots(currentCDPowner);

            currentCDPowner = sortedCDPs.getPrev(currentCDPowner);
        }
    }
}

contracts/SortedCDPs.sol.content:
pragma solidity 0.5.16;

import "./Interfaces/ISortedCDPs.sol";
import "./Interfaces/ITroveManager.sol";
import "./Interfaces/IBorrowerOperations.sol";
import "./Dependencies/SafeMath.sol";
import "./Dependencies/Ownable.sol";
import "./Dependencies/console.sol";

/* 
A sorted doubly linked list with nodes sorted in descending order.

Nodes map to active CDPs in the system - the ID property is the address of a CDP owner. 
Nodes are ordered according to their current individual collateral ratio (ICR).

The list optionally accepts insert position hints.

ICRs are computed dynamically at runtime, and not stored on the Node. This is because ICRs of active CDPs 
change dynamically as liquidation events occur.

The list relies on the fact that liquidation events preserve ordering: a liquidation decreases the ICRs of all active CDPs, 
but maintains their order. A node inserted based on current ICR will maintain the correct position, 
relative to it's peers, as rewards accumulate, as long as it's raw collateral and debt have not changed.
Thus, Nodes remain sorted by current ICR.

Nodes need only be re-inserted upon a CDP operation - when the owner adds or removes collateral or debt 
to their position.

The list is a modification of the following audited SortedDoublyLinkedList:
https://github.com/livepeer/protocol/blob/master/contracts/libraries/SortedDoublyLL.sol


Changes made in the Liquity implementation:

- Keys have been removed from nodes

- Ordering checks for insertion are performed by comparing an ICR argument to the current ICR, calculated at runtime. 
The list relies on the property that ordering by ICR is maintained as the ETH:USD price varies.

- Public functions with parameters have been made internal to save gas, and given an external wrapper function for external access
*/
contract SortedCDPs is Ownable, ISortedCDPs {
    using SafeMath for uint256;

    event TroveManagerAddressChanged(address _newCDPlManagerAddress);
    event BorrowerOperationsAddressChanged(address _borrowerOperationsAddress);

    address public borrowerOperationsAddress;

    ITroveManager public troveManager;
    address public TroveManagerAddress;

    // Information for a node in the list
    struct Node {
        bool exists;
        address nextId;                  // Id of next node (smaller ICR) in the list
        address prevId;                  // Id of previous node (larger ICR) in the list
    }

    // Information for the list
    struct Data {
        address head;                        // Head of the list. Also the node in the list with the largest ICR
        address tail;                        // Tail of the list. Also the node in the list with the smallest ICR
        uint256 maxSize;                     // Maximum size of the list
        uint256 size;                        // Current size of the list
        mapping (address => Node) nodes;     // Track the corresponding ids for each node in the list
    }

    Data public data;

    // --- Modifiers ---

    modifier onlyBorrowerOperations() {
        require(_msgSender() == borrowerOperationsAddress, "SortedCDPs: Caller is not the BorrowerOperations contract");
        _;
    }

    modifier onlyTroveManager() {
        require(_msgSender() == TroveManagerAddress, "SortedCDPs: Caller is not the TroveManager");
        _;
    }

     modifier onlyBOorCDPM() {
        address sender = _msgSender();
        require(sender == borrowerOperationsAddress || sender == TroveManagerAddress, 
                "SortedCDPs: Caller is neither BO nor CDPM");
        _;
    }

    // --- Dependency setters --- 

    function setParams(uint256 _size, address _TroveManagerAddress, address _borrowerOperationsAddress) external onlyOwner {
        require(_size > 0, "SortedCDPs: Size canâ€™t be zero");

        data.maxSize = _size;

        TroveManagerAddress = _TroveManagerAddress;
        troveManager = ITroveManager(_TroveManagerAddress);
        borrowerOperationsAddress = _borrowerOperationsAddress;

        emit TroveManagerAddressChanged(_TroveManagerAddress);
        emit BorrowerOperationsAddressChanged(_borrowerOperationsAddress);

        _renounceOwnership();
    }

    /*
     * @dev Add a node to the list
     * @param _id Node's id
     * @param _ICR Node's ICR
     * @param _prevId Id of previous node for the insert position
     * @param _nextId Id of next node for the insert position
     */

    function insert (address _id, uint256 _ICR, uint _price, address _prevId, address _nextId) external onlyBOorCDPM {
        _insert (_id, _ICR, _price, _prevId, _nextId);
    }

    function _insert(address _id, uint256 _ICR, uint _price, address _prevId, address _nextId) internal {
        // List must not be full
        require(!isFull());  
        // List must not already contain node
        require(!_contains(_id));  
        // Node id must not be null
        require(_id != address(0));  
        // ICR must be non-zero
        require(_ICR > 0); 

        address prevId = _prevId; 
        address nextId = _nextId; 

        if (!_validInsertPosition(_ICR, _price, prevId, nextId)) {
            // Sender's hint was not a valid insert position
            // Use sender's hint to find a valid insert position
            (prevId, nextId) = _findInsertPosition(_ICR, _price, prevId, nextId);   
        }

         data.nodes[_id].exists = true;  

        if (prevId == address(0) && nextId == address(0)) {
            // Insert as head and tail
            data.head = _id; 
            data.tail = _id; 
        } else if (prevId == address(0)) { 
            // Insert before `prevId` as the head
            data.nodes[_id].nextId = data.head; 
            data.nodes[data.head].prevId = _id;  
            data.head = _id; 
        } else if (nextId == address(0)) {
            // Insert after `nextId` as the tail
            data.nodes[_id].prevId = data.tail;
            data.nodes[data.tail].nextId = _id;
            data.tail = _id;
        } else { 
            // Insert at insert position between `prevId` and `nextId`
            data.nodes[_id].nextId = nextId; 
            data.nodes[_id].prevId = prevId; 
            data.nodes[prevId].nextId = _id; 
            data.nodes[nextId].prevId = _id; 
        }

        data.size = data.size.add(1); 
    }

    function remove(address _id) external onlyTroveManager {
        _remove(_id);
    }

    /*
     * @dev Remove a node from the list
     * @param _id Node's id
     */
    function _remove(address _id) internal {
        // List must contain the node
        require(_contains(_id)); 

        if (data.size > 1) { 
            // List contains more than a single node
            if (_id == data.head) { 
                // The removed node is the head
                // Set head to next node
                data.head = data.nodes[_id].nextId; 
                // Set prev pointer of new head to null
                data.nodes[data.head].prevId = address(0); 
            } else if (_id == data.tail) {
                // The removed node is the tail
                // Set tail to previous node
                data.tail = data.nodes[_id].prevId;
                // Set next pointer of new tail to null
                data.nodes[data.tail].nextId = address(0);
            } else {
                // The removed node is neither the head nor the tail
                // Set next pointer of previous node to the next node
                data.nodes[data.nodes[_id].prevId].nextId = data.nodes[_id].nextId;
                // Set prev pointer of next node to the previous node
                data.nodes[data.nodes[_id].nextId].prevId = data.nodes[_id].prevId;
            }
        } else {
            // List contains a single node
            // Set the head and tail to null
            data.head = address(0);
            data.tail = address(0);
        }

        delete data.nodes[_id]; 
        data.size = data.size.sub(1); 
    }

    /*
     * @dev Re-insert the node at a new position, based on its new ICR
     * @param _id Node's id
     * @param _newICR Node's new ICR
     * @param _prevId Id of previous node for the new insert position
     * @param _nextId Id of next node for the new insert position
     */
    function reInsert(address _id, uint256 _newICR, uint _price, address _prevId, address _nextId) external onlyBOorCDPM {
        // List must contain the node
        require(_contains(_id));

        // Remove node from the list
        _remove(_id);

        if (_newICR > 0) {
            // Insert node if it has a non-zero ICR
            _insert(_id, _newICR, _price, _prevId, _nextId);
        }
    }

    /*
     * @dev Checks if the list contains a node
     * @param _transcoder Address of transcoder
     */
    function contains(address _id) external view returns (bool) {
       return _contains(_id);
    }

    function _contains(address _id) internal view returns (bool) {
        return data.nodes[_id].exists;
    }

    /*
     * @dev Checks if the list is full
     */
    function isFull() public view returns (bool) {
        return data.size == data.maxSize;
    }

    /*
     * @dev Checks if the list is empty
     */
    function isEmpty() public view returns (bool) {
        return data.size == 0;
    }

    /*
     * @dev Returns the current size of the list
     */
    function getSize() external view returns (uint256) {
        return data.size;
    }

    /*
     * @dev Returns the maximum size of the list
     */
    function getMaxSize() external view returns (uint256) {
        return data.maxSize;
    }

    /*
     * @dev Returns the first node in the list (node with the largest ICR)
     */
    function getFirst() external view returns (address) {
        return data.head;
    }

    /*
     * @dev Returns the last node in the list (node with the smallest ICR)
     */
    function getLast() external view returns (address) {
        return data.tail;
    }

    /*
     * @dev Returns the next node (with a smaller ICR) in the list for a given node
     * @param _id Node's id
     */
    function getNext(address _id) external view returns (address) {
        return data.nodes[_id].nextId;
    }

    /*
     * @dev Returns the previous node (with a larger ICR) in the list for a given node
     * @param _id Node's id
     */
    function getPrev(address _id) external view returns (address) {
        return data.nodes[_id].prevId;
    }

    /*
     * @dev Check if a pair of nodes is a valid insertion point for a new node with the given ICR
     * @param _ICR Node's ICR
     * @param _prevId Id of previous node for the insert position
     * @param _nextId Id of next node for the insert position
     */

    function validInsertPosition(uint256 _ICR, uint _price, address _prevId, address _nextId) external view returns (bool) {
        return _validInsertPosition(_ICR, _price, _prevId, _nextId);
    }

    function _validInsertPosition(uint256 _ICR, uint _price, address _prevId, address _nextId) internal view returns (bool) {
        if (_prevId == address(0) && _nextId == address(0)) {
            // `(null, null)` is a valid insert position if the list is empty
            return isEmpty();
        } else if (_prevId == address(0)) {
            // `(null, _nextId)` is a valid insert position if `_nextId` is the head of the list
            return data.head == _nextId && _ICR >= troveManager.getCurrentICR(_nextId, _price);
        } else if (_nextId == address(0)) {
            // `(_prevId, null)` is a valid insert position if `_prevId` is the tail of the list
            return data.tail == _prevId && _ICR <= troveManager.getCurrentICR(_prevId, _price);
        } else {
            // `(_prevId, _nextId)` is a valid insert position if they are adjacent nodes and `_ICR` falls between the two nodes' ICRs
            return data.nodes[_prevId].nextId == _nextId && 
                   troveManager.getCurrentICR(_prevId, _price) >= _ICR && 
                   _ICR >= troveManager.getCurrentICR(_nextId, _price);
        }
    }

    /*
     * @dev Descend the list (larger ICRs to smaller ICRs) to find a valid insert position
     * @param _ICR Node's ICR
     * @param _startId Id of node to start ascending the list from
     */
    function descendList(uint256 _ICR, uint _price, address _startId) private view returns (address, address) {
        // If `_startId` is the head, check if the insert position is before the head
        if (data.head == _startId && _ICR >= troveManager.getCurrentICR(_startId, _price)) {
            return (address(0), _startId);
        }

        address prevId = _startId;
        address nextId = data.nodes[prevId].nextId;

        // Descend the list until we reach the end or until we find a valid insert position
        while (prevId != address(0) && !_validInsertPosition(_ICR, _price, prevId, nextId)) {
            prevId = data.nodes[prevId].nextId;
            nextId = data.nodes[prevId].nextId;
        }

        return (prevId, nextId);
    }

    /*
     * @dev Ascend the list (smaller ICRs to larger ICRs) to find a valid insert position
     * @param _ICR Node's ICR
     * @param _startId Id of node to start descending the list from
     */
    function ascendList(uint256 _ICR, uint _price, address _startId) private view returns (address, address) {
        // If `_startId` is the tail, check if the insert position is after the tail
        if (data.tail == _startId && _ICR <= troveManager.getCurrentICR(_startId, _price)) {
            return (_startId, address(0));
        }

        address nextId = _startId;
        address prevId = data.nodes[nextId].prevId;

        // Ascend the list until we reach the end or until we find a valid insertion point
        while (nextId != address(0) && !_validInsertPosition(_ICR, _price, prevId, nextId)) {
            nextId = data.nodes[nextId].prevId;
            prevId = data.nodes[nextId].prevId;
        }

        return (prevId, nextId);
    }

    /*
     * @dev Find the insert position for a new node with the given ICR
     * @param _ICR Node's ICR
     * @param _prevId Id of previous node for the insert position
     * @param _nextId Id of next node for the insert position
     */
    function findInsertPosition(uint256 _ICR, uint _price, address _prevId, address _nextId) external view returns (address, address) {
        return _findInsertPosition(_ICR, _price, _prevId, _nextId);
    }

    function _findInsertPosition(uint256 _ICR, uint _price, address _prevId, address _nextId) internal view returns (address, address) {
        address prevId = _prevId;
        address nextId = _nextId;

        if (prevId != address(0)) {
            if (!_contains(prevId) || _ICR > troveManager.getCurrentICR(prevId, _price)) {
                // `prevId` does not exist anymore or now has a smaller ICR than the given ICR
                prevId = address(0);
            }
        }

        if (nextId != address(0)) {
            if (!_contains(nextId) || _ICR < troveManager.getCurrentICR(nextId, _price)) {
                // `nextId` does not exist anymore or now has a larger ICR than the given ICR
                nextId = address(0);
            }
        }

        if (prevId == address(0) && nextId == address(0)) {
            // No hint - descend list starting from head
            return descendList(_ICR, _price, data.head);
        } else if (prevId == address(0)) {
            // No `prevId` for hint - ascend list starting from `nextId`
            return ascendList(_ICR, _price, nextId);
        } else if (nextId == address(0)) {
            // No `nextId` for hint - descend list starting from `prevId`
            return descendList(_ICR, _price, prevId);
        } else {
            // Descend list starting from `prevId`
            return descendList(_ICR, _price, prevId);
        }
    }
}

contracts/PoolManager.sol.content:
pragma solidity 0.5.16;

import './Interfaces/IBorrowerOperations.sol';
import './Interfaces/IPool.sol';
import './Interfaces/IPoolManager.sol';
import './Interfaces/ITroveManager.sol';
import './Interfaces/IStabilityPool.sol';
import './Interfaces/IPriceFeed.sol';
import './Interfaces/ILUSDToken.sol';
import './Dependencies/Math.sol';
import './Dependencies/SafeMath.sol';
import './Dependencies/SafeMath128.sol';
import "./Dependencies/Ownable.sol";
import "./Dependencies/console.sol";

// PoolManager maintains all pools 
contract PoolManager is Ownable, IPoolManager {
    using SafeMath for uint;
    using SafeMath128 for uint128;

    address constant public GAS_POOL_ADDRESS = 0x00000000000000000000000000000000000009A5;

    // --- Connected contract declarations ---

    IBorrowerOperations public borrowerOperations;
    address public borrowerOperationsAddress;

    address public troveManagerAddress;
    ITroveManager public troveManager;

    IPriceFeed public priceFeed;

    ILUSDToken public CLV;

    IStabilityPool public stabilityPool;
    address public stabilityPoolAddress;

    IPool public activePool;
    address public activePoolAddress;

    IPool public defaultPool;
    address public defaultPoolAddress;
   // --- Data structures ---

    mapping (address => uint) public initialDeposits;

    struct Snapshot {
        uint S;
        uint P;
        uint128 scale;
        uint128 epoch;
    }

    /* Product 'P': Running product by which to multiply an initial deposit, in order to find the current compounded deposit, 
    given a series of liquidations, each of which cancel some CLV debt with the deposit. 

    During its lifetime, a deposit's value evolves from d(0) to (d(0) * P / P(0) ), where P(0) 
    is the snapshot of P taken at the instant the deposit was made. 18 DP decimal.  */
    uint public P = 1e18;

     // Each time the scale of P shifts by 1e18, the scale is incremented by 1
    uint128 public currentScale; 

    // With each offset that fully empties the Pool, the epoch is incremented by 1
    uint128 public currentEpoch;  

    /* ETH Gain sum 'S': During it's lifetime, each deposit d(0) earns an ETH gain of ( d(0) * [S - S(0)] )/P(0), where S(0) 
    is the snapshot of S taken at the instant the deposit was made.

    The 'S' sums are stored in a nested mapping (epoch => scale => sum):

    - The inner mapping records the sum S at different scales
    - The outer mapping records the (scale => sum) mappings, for different epochs. */
    mapping (uint => mapping(uint => uint)) public epochToScaleToSum;

    // Map users to their individual snapshot structs
    mapping (address => Snapshot) public snapshot;

    // Error trackers for the error correction in the offset calculation
    uint public lastETHError_Offset;
    uint public lastCLVLossError_Offset;

    // --- Events ---

    event BorrowerOperationsAddressChanged(address _newBorrowerOperationsAddress);
    event TroveManagerAddressChanged(address _newTroveManagerAddress);
    event PriceFeedAddressChanged(address _newPriceFeedAddress);
    event LUSDTokenAddressChanged(address _newLUSDTokenAddress);
    event StabilityPoolAddressChanged(address _newStabilityPoolAddress);
    event ActivePoolAddressChanged(address _newActivePoolAddress);
    event DefaultPoolAddressChanged(address _newDefaultPoolAddress);

    event UserSnapshotUpdated(uint _P, uint _S);
    event P_Updated(uint _P);
    event S_Updated(uint _S);
    event UserDepositChanged(address indexed _user, uint _amount);
    event ETHGainWithdrawn(address indexed _user, uint _ETH, uint _CLVLoss);

    // --- Modifiers ---

    modifier onlyTroveManager() {
        require(_msgSender() == troveManagerAddress, "PoolManager: Caller is not the TroveManager");
        _;
    }

     modifier onlyBorrowerOperations() {
        require(_msgSender() == borrowerOperationsAddress, "PoolManager: Caller is not the BorrowerOperations contract");
        _;
    }

    modifier onlyStabilityPool {
        require(
            _msgSender() == stabilityPoolAddress,
            "PoolManager: Caller is not StabilityPool");
        _;
    }

    // --- Dependency setters ---

    function setAddresses(
        address _borrowerOperationsAddress,
        address _troveManagerAddress,
        address _priceFeedAddress,
        address _CLVAddress,
        address _stabilityPoolAddress,
        address _activePoolAddress,
        address _defaultPoolAddress
    )
    external
    onlyOwner
    {
        borrowerOperationsAddress = _borrowerOperationsAddress;
        borrowerOperations = IBorrowerOperations(_borrowerOperationsAddress);
        troveManagerAddress = _troveManagerAddress;
        troveManager = ITroveManager(_troveManagerAddress);
        priceFeed = IPriceFeed(_priceFeedAddress);
        CLV = ILUSDToken(_CLVAddress);
        stabilityPoolAddress = _stabilityPoolAddress;
        stabilityPool = IStabilityPool(_stabilityPoolAddress);
        activePoolAddress = _activePoolAddress;
        activePool = IPool(_activePoolAddress);
        defaultPoolAddress = _defaultPoolAddress;
        defaultPool = IPool(_defaultPoolAddress);

        emit BorrowerOperationsAddressChanged(_borrowerOperationsAddress);
        emit TroveManagerAddressChanged(_troveManagerAddress);
        emit PriceFeedAddressChanged(_priceFeedAddress);
        emit LUSDTokenAddressChanged(_CLVAddress);
        emit StabilityPoolAddressChanged(_stabilityPoolAddress);
        emit ActivePoolAddressChanged(_activePoolAddress);
        emit DefaultPoolAddressChanged(_defaultPoolAddress);

        _renounceOwnership();
    }

    // --- Getters ---

    // Return the total active debt (in CLV) in the system
    function getActiveDebt() external view returns (uint) {
        return activePool.getCLVDebt();
    }    

    // Return the total active collateral (in ETH) in the system
    function getActiveColl() external view returns (uint) {
        return activePool.getETH();
    } 

    // Return the amount of closed debt (in CLV)
    function getClosedDebt() external view returns (uint) {
        return defaultPool.getCLVDebt();
    }    

    // Return the amount of closed collateral (in ETH)
    function getLiquidatedColl() external view returns (uint) {
        return defaultPool.getETH();
    }

    // Return the total CLV in the Stability Pool
    function getStabilityPoolCLV() external view returns (uint) {
        return stabilityPool.getTotalCLVDeposits();
    }

    // --- Pool interaction functions ---

    // Add the received ETH to the total active collateral
    function addColl() external payable onlyBorrowerOperations {
        // Send ETH to Active Pool and increase its recorded ETH balance
       (bool success, ) = activePoolAddress.call.value(msg.value)("");
       assert(success == true);
    }

    // Transfer the specified amount of ETH to _account and updates the total active collateral
    function withdrawColl(address _account, uint _ETH) external onlyBorrowerOperations {
        activePool.sendETH(_account, _ETH);
    }

    // Issue the specified amount of CLV to _account and increases the total active debt
    function withdrawCLV(address _account, uint _CLV) external onlyBorrowerOperations {
        _withdrawCLV(_account, _CLV);
    }

    function _withdrawCLV(address _account, uint _CLV) internal {
        activePool.increaseCLVDebt(_CLV);
        CLV.mint(_account, _CLV);
    }

    // Burn the specified amount of CLV from _account and decreases the total active debt
    function repayCLV(address _account, uint _CLV) external onlyBorrowerOperations {
        _repayCLV(_account, _CLV);
    }

    function _repayCLV(address _account, uint _CLV) internal {
        activePool.decreaseCLVDebt(_CLV);
        CLV.burn(_account, _CLV);
    }

    function lockCLVGasCompensation(uint _CLV) external onlyBorrowerOperations {
        _withdrawCLV(GAS_POOL_ADDRESS, _CLV);
    }

    function refundCLVGasCompensation(uint _CLV) external onlyBorrowerOperations {
        _repayCLV(GAS_POOL_ADDRESS, _CLV);
    }

    function sendCLVGasCompensation(address _user, uint _CLV) external onlyTroveManager {
        CLV.returnFromPool(GAS_POOL_ADDRESS, _user, _CLV);
    }

    // Update the Active Pool and the Default Pool when a CDP gets liquidated
    function liquidate(uint _CLV, uint _ETH) external onlyTroveManager {
        // Transfer the debt & coll from the Active Pool to the Default Pool
        defaultPool.increaseCLVDebt(_CLV);
        activePool.decreaseCLVDebt(_CLV);
        activePool.sendETH(defaultPoolAddress, _ETH);
    }

    // Move a CDP's pending debt and collateral rewards from distributions, from the Default Pool to the Active Pool
    function movePendingTroveRewardsToActivePool(uint _CLV, uint _ETH) external onlyTroveManager {
        // Transfer the debt & coll from the Default Pool to the Active Pool
        defaultPool.decreaseCLVDebt(_CLV);  
        activePool.increaseCLVDebt(_CLV); 
        defaultPool.sendETH(activePoolAddress, _ETH); 
    }

    // Burn the received CLV, transfers the redeemed ETH to _account and updates the Active Pool
    function redeemCollateral(address _account, uint _CLV, uint _ETH) external onlyTroveManager {
        // Update Active Pool CLV, and send ETH to account
        CLV.burn(_account, _CLV); 
        activePool.decreaseCLVDebt(_CLV);  

        activePool.sendETH(_account, _ETH); 
    }

    /*
      Burn the remaining gas compensation CLV, transfers the remaining ETH to _account and updates the Active Pool
     * Itâ€™s called by TroveManager when after redemption thereâ€™s only gas compensation left as debt
     */
    function redeemCloseLoan(address _account, uint _CLV, uint _ETH) external onlyTroveManager {
        /*
         * This is called by TroveManager when the redemption drains all the trove and thereâ€™s only the gas compensation left.
         * The redeemer swaps (debt - 10) CLV for (debt - 10) worth of ETH, so the 10 CLV gas compensation left correspond to the remaining collateral.
         * In order to close the trove, the user should get the CLV refunded and use them to repay and close it,
         * but instead we do that all in one step.
         */
        CLV.burn(GAS_POOL_ADDRESS, _CLV);
        // Update Active Pool CLV, and send ETH to account
        activePool.decreaseCLVDebt(_CLV);

        activePool.sendETH(_account, _ETH);
    }

    // Transfer the CLV tokens from the user to the Stability Pool's address, and update its recorded CLV
    function _sendCLVtoStabilityPool(address _address, uint _amount) internal {
        CLV.sendToPool(_address, stabilityPoolAddress, _amount);
        stabilityPool.increaseCLV(_amount);
    }

    // --- Reward calculator functions ---

    function getCurrentETHGain(address _user) external view returns (uint) {
        return _getCurrentETHGain(_user);
    }

    /* Return the ETH gain earned by the deposit. Given by the formula:  E = d0 * (S - S(0))/P(0)
    where S(0) and P(0) are the depositor's snapshots of the sum S and product P, respectively. */
    function _getCurrentETHGain(address _user) internal view returns (uint) {
        uint initialDeposit = initialDeposits[_user];

        if (initialDeposit == 0) { return 0; }

        uint snapshot_S = snapshot[_user].S;  
        uint snapshot_P = snapshot[_user].P;
        uint scaleSnapshot = snapshot[_user].scale;
        uint epochSnapshot = snapshot[_user].epoch;

        uint ETHGain;

        /* Grab the reward sum from the epoch at which the deposit was made. The reward may span up to 
        one scale change.  
        If it does, the second portion of the reward is scaled by 1e18. 
        If the reward spans no scale change, the second portion will be 0. */
        uint firstPortion = epochToScaleToSum[epochSnapshot][scaleSnapshot].sub(snapshot_S);
        uint secondPortion = epochToScaleToSum[epochSnapshot][scaleSnapshot.add(1)].div(1e18);

        ETHGain = initialDeposit.mul(firstPortion.add(secondPortion)).div(snapshot_P).div(1e18);

        return ETHGain;
    }

    function getCompoundedCLVDeposit(address _user) external view returns (uint) {
        return _getCompoundedCLVDeposit(_user);
    }

    /* Return the user's compounded deposit.  Given by the formula:  d = d0 * P/P(0)
    where P(0) is the depositor's snapshot of the product P. */
    function _getCompoundedCLVDeposit(address _user) internal view returns (uint) {
        uint initialDeposit = initialDeposits[_user];

        if (initialDeposit == 0) { return 0; }

        uint snapshot_P = snapshot[_user].P; 
        uint128 scaleSnapshot = snapshot[_user].scale;
        uint128 epochSnapshot = snapshot[_user].epoch;

        // If deposit was made before a pool-emptying event, then it has been fully cancelled with debt -- so, return 0
        if (epochSnapshot < currentEpoch) { return 0; }

        uint compoundedDeposit;
        uint128 scaleDiff = currentScale.sub(scaleSnapshot);

        /* Compute the compounded deposit. If a scale change in P was made during the deposit's lifetime, 
        account for it. If more than one scale change was made, then the deposit has decreased by a factor of 
        at least 1e-18 -- so return 0.*/
        if (scaleDiff == 0) { 
            compoundedDeposit = initialDeposit.mul(P).div(snapshot_P);
        } else if (scaleDiff == 1) {
            compoundedDeposit = initialDeposit.mul(P).div(snapshot_P).div(1e18);
        } else {
            compoundedDeposit = 0;
        }

        // If compounded deposit is less than a billionth of the initial deposit, return 0
        if (compoundedDeposit < initialDeposit.div(1e9)) { return 0; }

        return compoundedDeposit;
    }

    // --- Sender functions for CLV deposits and ETH gains ---

    function _sendETHGainToUser(address _address, uint ETHGain) internal {
        stabilityPool.sendETH(_address, ETHGain);
    }

    // Send ETHGain to CDP. Send in two steps: StabilityPool -> PoolManager -> user's CDP
    function _sendETHGainToCDP(address _address, uint _ETHGain, address _hint) internal {
        stabilityPool.sendETH(address(this), _ETHGain); 
        borrowerOperations.addColl.value(_ETHGain)(_address, _hint); 
    }

    // Send CLV to user and decrease CLV in Pool
    function _sendCLVToUser(address _address, uint CLVWithdrawal) internal {
        uint CLVinPool = stabilityPool.getTotalCLVDeposits();
        assert(CLVWithdrawal <= CLVinPool);

        CLV.returnFromPool(stabilityPoolAddress, _address, CLVWithdrawal); 
        stabilityPool.decreaseCLV(CLVWithdrawal);
    }

    // --- Stability Pool Deposit Functionality --- 

    // Record a new deposit
    function _updateDeposit(address _address, uint _amount) internal {
        if (_amount == 0) {
            initialDeposits[_address] = 0;
            emit UserSnapshotUpdated(snapshot[_address].P, snapshot[_address].S);
            return;
        }

        initialDeposits[_address] = _amount;

        // Record new individual snapshots of the running product P and sum S for the user
        snapshot[_address].P = P;
        snapshot[_address].S = epochToScaleToSum[currentEpoch][currentScale];
        snapshot[_address].scale = currentScale;
        snapshot[_address].epoch = currentEpoch;

        emit UserSnapshotUpdated(snapshot[_address].P, snapshot[_address].S);
    }

    // --- External StabilityPool Functions ---

    /* Send ETHGain to user's address, and updates their deposit, 
    setting newDeposit = compounded deposit + amount. */
    function provideToSP(uint _amount) external {
        address user = _msgSender();
        uint initialDeposit = initialDeposits[user];

        if (initialDeposit == 0) {
            _sendCLVtoStabilityPool(user, _amount);
            _updateDeposit(user, _amount);

            emit UserDepositChanged(user, _amount);

        } else { // If user already has a deposit, make a new composite deposit and retrieve their ETH gain
            uint compoundedCLVDeposit = _getCompoundedCLVDeposit(user);
            uint CLVLoss = initialDeposit.sub(compoundedCLVDeposit);
            uint ETHGain = _getCurrentETHGain(user);

            uint newDeposit = compoundedCLVDeposit.add(_amount);

            _sendCLVtoStabilityPool(user, _amount);
            _updateDeposit(user, newDeposit);

            _sendETHGainToUser(user, ETHGain);

            emit ETHGainWithdrawn(user, ETHGain, CLVLoss);
            emit UserDepositChanged(user, newDeposit); 
        }
    }

    /* Withdraw _amount of CLV and the callerâ€™s entire ETH gain from the 
    Stability Pool, and updates the callerâ€™s reduced deposit. 

    If  _amount is 0, the user only withdraws their ETH gain, no CLV.
    If _amount > userDeposit, the user withdraws all their ETH gain, and all of their compounded deposit.

    In all cases, the entire ETH gain is sent to user. */
    function withdrawFromSP(uint _amount) external {
        address user = _msgSender();
        _requireUserHasDeposit(user); 

        uint initialDeposit = initialDeposits[user];
        uint compoundedCLVDeposit = _getCompoundedCLVDeposit(user);
        uint CLVLoss = initialDeposit.sub(compoundedCLVDeposit);
        uint ETHGain = _getCurrentETHGain(user);

        uint CLVtoWithdraw = Math._min(_amount, compoundedCLVDeposit);
        uint CLVremainder = compoundedCLVDeposit.sub(CLVtoWithdraw);

        _sendCLVToUser(user, CLVtoWithdraw);
        _updateDeposit(user, CLVremainder);

        _sendETHGainToUser(user, ETHGain);

        emit ETHGainWithdrawn(user, ETHGain, CLVLoss);
        emit UserDepositChanged(user, CLVremainder); 
    }

    /* Transfer the caller's entire ETH gain from the Stability Pool to the caller's CDP, and leaves
    their compounded deposit in the Stability Pool.

    TODO: Remove _user param and just use _msgSender(). */
    function withdrawFromSPtoCDP(address _user, address _hint) external {
        require(_user == _msgSender(), "PoolManager: A user may only withdraw ETH gains to their own trove" );
        _requireUserHasDeposit(_user); 
        _requireUserHasTrove(_user);

        uint initialDeposit = initialDeposits[_user];
        uint compoundedCLVDeposit = _getCompoundedCLVDeposit(_user);
        uint CLVLoss = initialDeposit.sub(compoundedCLVDeposit);
        uint ETHGain = _getCurrentETHGain(_user);

        // Update the recorded deposit value, and deposit snapshots
        _updateDeposit(_user, compoundedCLVDeposit);

        /* Emit events before transferring ETH gain to CDP.
         This lets the event log make more sense (i.e. so it appears that first the ETH gain is withdrawn 
        and then it is deposited into the CDP, not the other way around). */
        emit ETHGainWithdrawn(_user, ETHGain, CLVLoss);
        emit UserDepositChanged(_user, compoundedCLVDeposit); 

        _sendETHGainToCDP(_user, ETHGain, _hint);
    }

     /* Cancel out the specified _debt against the CLV contained in the Stability Pool (as far as possible)  
    and transfers the CDP's ETH collateral from ActivePool to StabilityPool. 
    Only called from liquidation functions in TroveManager. */
    function offset(uint _debtToOffset, uint _collToAdd) 
    external 
    onlyTroveManager
    {    
        uint totalCLVDeposits = stabilityPool.getTotalCLVDeposits();
        if (totalCLVDeposits == 0 || _debtToOffset == 0) { return; }

        (uint ETHGainPerUnitStaked,
         uint CLVLossPerUnitStaked) = _computeRewardsPerUnitStaked(_collToAdd, _debtToOffset, totalCLVDeposits);

        _updateRewardSumAndProduct(ETHGainPerUnitStaked, CLVLossPerUnitStaked);

        _moveOffsetCollAndDebt(_collToAdd, _debtToOffset);
    }

    // --- Offset helper functions ---

    function _computeRewardsPerUnitStaked(uint _collToAdd, uint _debtToOffset, uint _totalCLVDeposits) 
    internal 
    returns (uint ETHGainPerUnitStaked, uint CLVLossPerUnitStaked) 
    {
        uint CLVLossNumerator = _debtToOffset.mul(1e18).sub(lastCLVLossError_Offset);
        uint ETHNumerator = _collToAdd.mul(1e18).add(lastETHError_Offset);

        // Compute the CLV and ETH rewards, and error corrections
        if (_debtToOffset >= _totalCLVDeposits) {
            CLVLossPerUnitStaked = 1e18;
            lastCLVLossError_Offset = 0;
        } else {
            CLVLossPerUnitStaked = (CLVLossNumerator.div(_totalCLVDeposits)).add(1); // add 1 to make error in quotient positive
             lastCLVLossError_Offset = (CLVLossPerUnitStaked.mul(_totalCLVDeposits)).sub(CLVLossNumerator);
        } 

        ETHGainPerUnitStaked = ETHNumerator.div(_totalCLVDeposits); 
        lastETHError_Offset = ETHNumerator.sub(ETHGainPerUnitStaked.mul(_totalCLVDeposits)); 

        return (ETHGainPerUnitStaked, CLVLossPerUnitStaked);
    }

    // Update the Stability Pool reward sum S and product P
    function _updateRewardSumAndProduct(uint _ETHGainPerUnitStaked, uint _CLVLossPerUnitStaked) internal {
         // Make product factor 0 if there was a pool-emptying. Otherwise, it is (1 - CLVLossPerUnitStaked)
        uint newProductFactor = _CLVLossPerUnitStaked >= 1e18 ? 0 : uint(1e18).sub(_CLVLossPerUnitStaked);

        // Update the ETH reward sum at the current scale and current epoch
        uint marginalETHGain = _ETHGainPerUnitStaked.mul(P);
        epochToScaleToSum[currentEpoch][currentScale] = epochToScaleToSum[currentEpoch][currentScale].add(marginalETHGain);
        emit S_Updated(epochToScaleToSum[currentEpoch][currentScale]); 

       // If the Pool was emptied, increment the epoch and reset the scale and product P
        if (newProductFactor == 0) {
            currentEpoch = currentEpoch.add(1);
            currentScale = 0;
            P = 1e18;

        // If multiplying P by a non-zero product factor would round P to zero, increment the scale 
        } else if (P.mul(newProductFactor) < 1e18) {
            P = P.mul(newProductFactor);
            currentScale = currentScale.add(1);
         } else {
            P = P.mul(newProductFactor).div(1e18); 
        }

        emit P_Updated(P); 
    }

    function _moveOffsetCollAndDebt(uint _collToAdd, uint _debtToOffset) internal {
        // Cancel the liquidated CLV debt with the CLV in the stability pool
        activePool.decreaseCLVDebt(_debtToOffset);  
        stabilityPool.decreaseCLV(_debtToOffset); 

        // Send ETH from Active Pool to Stability Pool
        activePool.sendETH(stabilityPoolAddress, _collToAdd);  

        // Burn the debt that was successfully offset
        CLV.burn(stabilityPoolAddress, _debtToOffset); 
    }

    // --- 'require' wrapper functions ---

    function _requireUserHasDeposit(address _address) internal view {
        uint initialDeposit = initialDeposits[_address];  
        require(initialDeposit > 0, 'PoolManager: User must have a non-zero deposit');  
    }

    function _requireUserHasTrove(address _user) internal view {
        require(troveManager.getCDPStatus(_user) == 1, "TroveManager: caller must have an active trove to withdraw ETHGain to");
    }

    function () external payable onlyStabilityPool {}
}

contracts/PriceFeed.sol.content:
pragma solidity 0.5.16;


import "./Interfaces/ITroveManager.sol";
import "./Interfaces/IPriceFeed.sol";
import "./Interfaces/IDeployedAggregator.sol";
import "./Interfaces/AggregatorInterface.sol";
import "./Dependencies/SafeMath.sol";
import "./Dependencies/Ownable.sol";
import "./Dependencies/console.sol";

contract PriceFeed is Ownable, IPriceFeed {
    using SafeMath for uint256;

    uint256 constant DIGITS = 1e18;
    uint256 public price = 200 * DIGITS;

    address public troveManagerAddress;
    address public poolManagerAddress;

    // Mainnet Chainlink aggregator
    address public priceAggregatorAddress;
    IDeployedAggregator public priceAggregator;

    // Testnet Chainlink aggregator
    address public priceAggregatorAddress_Testnet;
    AggregatorInterface public priceAggregator_Testnet;

    event PriceUpdated(uint256 _newPrice);
    event TroveManagerAddressChanged(address _troveManagerAddress);
    event PoolManagerAddressChanged(address _poolManagerAddress);

    // --- Modifiers ---

    modifier onlyTroveManagerOrPoolManager {
        require(_msgSender() == troveManagerAddress ||_msgSender() == poolManagerAddress,
            "PriceFeed: Caller is neither TroveManager nor PoolManager"
        );
        _;
    }

    // --- Dependency setters ---

    function setAddresses(
        address _troveManagerAddress,
        address _poolManagerAddress,
        address _priceAggregatorAddress,
        address _priceAggregatorAddressTestnet
    )
        external
        onlyOwner
    {
        troveManagerAddress = _troveManagerAddress;
        poolManagerAddress = _poolManagerAddress;
        // Mainnet Chainlink address setter
        priceAggregatorAddress = _priceAggregatorAddress;
        priceAggregator = IDeployedAggregator(_priceAggregatorAddress);
        // Testnet Chainlink address setter
        priceAggregator_Testnet = AggregatorInterface(_priceAggregatorAddressTestnet);

        emit TroveManagerAddressChanged(_troveManagerAddress);
        emit PoolManagerAddressChanged(_poolManagerAddress);

        _renounceOwnership();
    }

    // --- Functions ---

    function getPrice() external view returns (uint256) {
        return price;
    }

    // --- DEVELOPMENT FUNCTIONALITY - TODO: remove before mainnet deployment.  ---

    // Manual external price setter. 
    function setPrice(uint256 _price) external returns (bool) {
        price = _price;
        emit PriceUpdated(price);
        return true;
    }

    // --- MAINNET FUNCTIONALITY ---

    // TODO: convert received Chainlink price to precision-18 before setting state variable
    function updatePrice() external onlyTroveManagerOrPoolManager returns (uint256) {
        price = getLatestPrice();
        emit PriceUpdated(price);
        return price;
    }

    function getLatestPrice() public view returns (uint256) {
        int256 intPrice = priceAggregator.currentAnswer();
        require(intPrice >= 0, "Price response from aggregator is negative int");

        return uint256(intPrice);
    }

    function getLatestAnswerID() external view returns (uint256) {
        return priceAggregator.latestCompletedAnswer();
    }

    // Get the block timestamp at which the reference price was last updated
    function getLatestTimestamp() external view returns (uint256) {
        return priceAggregator.updatedHeight();
    }

    // ---- ROPSTEN FUNCTIONALITY - TODO: Remove before Mainnet deployment ----

    function updatePrice_Testnet() external returns (uint256) {
        price = getLatestPrice_Testnet();
        emit PriceUpdated(price);
        return price;
    }

    function getLatestPrice_Testnet() public view returns (uint256) {
        int256 intPrice = priceAggregator_Testnet.latestAnswer();
        require( intPrice >= 0, "Price response from aggregator is negative int");

        return uint256(intPrice).mul(10000000000);
    }

    // Get the block timestamp at which the reference data was last updated
    function getLatestTimestamp_Testnet() external view returns (uint256) {
        uint256 latestTimestamp = priceAggregator_Testnet.latestTimestamp();

        return latestTimestamp;
    }

    // Get the past price from 'n' rounds ago
    function getPreviousPrice_Testnet(uint256 _n) external view returns (uint256) {
        uint256 latestAnswerID = priceAggregator_Testnet.latestRound();
        require(_n <= latestAnswerID, "Not enough history");

        int256 prevPrice = priceAggregator_Testnet.getAnswer(latestAnswerID - _n);
        require(prevPrice >= 0, "Price response from aggregator is negative int");

        return uint256(prevPrice).mul(10000000000);
    }

    // Get the block timestamp from the round that occurred 'n' rounds ago
    function getPreviousTimestamp_Testnet(uint256 _n) external view returns (uint256) {
        uint256 latestAnswerID = priceAggregator_Testnet.latestRound();
        require(_n <= latestAnswerID, "Not enough history");

        return priceAggregator_Testnet.getTimestamp(latestAnswerID - _n);
    }
}

contracts/StabilityPool.sol.content:
pragma solidity 0.5.16;

import './Interfaces/IStabilityPool.sol';
import "./Dependencies/SafeMath.sol";
import "./Dependencies/Ownable.sol";
import "./Dependencies/console.sol";

contract StabilityPool is Ownable, IStabilityPool {
    using SafeMath for uint256;

    address public poolManagerAddress;
    address public activePoolAddress;
    uint256 internal ETH;  // deposited ether tracker

    // Total CLV held in the pool. Changes when users deposit/withdraw, and when CDP debt is offset.
    uint256 internal totalCLVDeposits;

    // --- Modifiers ---

    modifier onlyPoolManager {
        require(_msgSender() == poolManagerAddress, "StabilityPool:  Caller is not the PoolManager");
        _;
    }

    modifier onlyActivePool {
        require(_msgSender() == activePoolAddress, "StabilityPool: Caller is not ActivePool");
        _;
    }

    // --- Contract setters ---

    function setAddresses(
        address _poolManagerAddress,
        address _activePoolAddress
    )
        external
        onlyOwner
    {
        poolManagerAddress = _poolManagerAddress;
        activePoolAddress = _activePoolAddress;

        emit PoolManagerAddressChanged(_poolManagerAddress);
        emit ActivePoolAddressChanged(_activePoolAddress);

        _renounceOwnership();
    }

    // --- Getters for public variables. Required by IPool interface ---

    function getETH() external view returns (uint) {
        return ETH;
    }

    function getTotalCLVDeposits() external view returns (uint) {
        return totalCLVDeposits;
    }

    // --- Pool functionality ---

    function sendETH(address _account, uint _amount) external onlyPoolManager {
        ETH = ETH.sub(_amount);
        emit ETHBalanceUpdated(ETH);
        emit EtherSent(_account, _amount);

        (bool success, ) = _account.call.value(_amount)("");  // use call.value()('') as per Consensys latest advice 
        require(success, "StabilityPool: sending ETH failed");
    }

    function increaseCLV(uint _amount) external onlyPoolManager () {
        totalCLVDeposits  = totalCLVDeposits.add(_amount);
        emit CLVBalanceUpdated(totalCLVDeposits);
    }

    function decreaseCLV(uint _amount) external onlyPoolManager () {
        totalCLVDeposits = totalCLVDeposits.sub(_amount);
        emit CLVBalanceUpdated(totalCLVDeposits);
    }

    /* Returns the raw ether balance at StabilityPool address.  
    Not necessarily equal to the ETH state variable - ether can be forcibly sent to contracts. */
    function getRawETHBalance() external view returns (uint) {
        return address(this).balance;
    }

    function () external payable onlyActivePool {
        ETH = ETH.add(msg.value);
    }
}

